import{_ as d,c as r,o as e,ah as a}from"./chunks/framework.D2dsaVOY.js";const p=JSON.parse('{"title":"Fanuc Focas Ethernet","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/fanuc-focas/fanuc-focas.md","filePath":"en_US/configuration/south-devices/fanuc-focas/fanuc-focas.md"}'),n={name:"en_US/configuration/south-devices/fanuc-focas/fanuc-focas.md"};function o(i,t,s,m,l,u){return e(),r("div",null,[...t[0]||(t[0]=[a('<h1 id="fanuc-focas-ethernet" tabindex="-1">Fanuc Focas Ethernet <a class="header-anchor" href="#fanuc-focas-ethernet" aria-label="Permalink to “Fanuc Focas Ethernet”">​</a></h1><p>The GridBeat FOCAS driver supports the use of the FOCAS2 protocol for data acquisition and writing for various types of FANUC devices (including multi-path devices). It supports coordinates, operating status, alarms, operation information, macro variables, parameters, diagnostic data, program data, tool offset, tooling, tool life, PMC, and more.</p><p><strong>Support arch</strong>: amd64, armv7</p><h2 id="parameter-configuration" tabindex="-1">Parameter Configuration <a class="header-anchor" href="#parameter-configuration" aria-label="Permalink to “Parameter Configuration”">​</a></h2><table tabindex="0"><thead><tr><th>Parameter</th><th>Description</th></tr></thead><tbody><tr><td>host</td><td>device ip address</td></tr><tr><td>port</td><td>device port, default 8193</td></tr><tr><td>timeout</td><td>connection timeout, default 3000ms</td></tr></tbody></table><h2 id="support-data-type" tabindex="-1">Support Data Type <a class="header-anchor" href="#support-data-type" aria-label="Permalink to “Support Data Type”">​</a></h2><ul><li>uint8</li><li>int8</li><li>uint16</li><li>int16</li><li>uint32</li><li>int32</li><li>uint64</li><li>int64</li><li>float</li><li>double</li><li>bit</li><li>string</li></ul><h2 id="cnc-data" tabindex="-1">CNC Data <a class="header-anchor" href="#cnc-data" aria-label="Permalink to “CNC Data”">​</a></h2><h3 id="address-format" tabindex="-1">Address Format <a class="header-anchor" href="#address-format" aria-label="Permalink to “Address Format”">​</a></h3><p><code>address[.m][.n][.l][@p]</code></p><p>The address can support up to 3 parameters, with the <code>@</code> symbol specifying the channel where the address is located.</p><table tabindex="0"><thead><tr><th>Tag Identifier (Address)</th><th>Description</th><th>Data Type</th><th>Parameters</th><th>Remarks</th></tr></thead><tbody><tr><td>actf</td><td>Actual feed rate</td><td>int64/uint64</td><td>-</td><td></td></tr><tr><td>absolute.m</td><td>Axis absolute position</td><td>int64/uint64</td><td>Axis number</td><td>Includes tool offset, may not match the display on the CNC</td></tr><tr><td>absolute2.m</td><td>Axis absolute position 2</td><td>int64/uint64</td><td>Axis number</td><td>Data is consistent with the CNC interface</td></tr><tr><td>machine.m</td><td>Axis machine position</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>relative.m</td><td>Axis relative position</td><td>int64/uint64</td><td>Axis number</td><td>Includes tool offset, may not match the display on the CNC</td></tr><tr><td>relative2.m</td><td>Axis relative position 2</td><td>int64/uint64</td><td>Axis number</td><td>Data is consistent with the CNC interface</td></tr><tr><td>distance.m</td><td>Axis remaining distance</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>acts</td><td>Actual spindle speed</td><td>int64/uint64</td><td>-</td><td></td></tr><tr><td>acts2.m</td><td>Actual spindle speed 2</td><td>int64/uint64</td><td>Spindle number</td><td></td></tr><tr><td>skip</td><td>Axis skip position</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>srvdelay</td><td>Axis servo delay</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>accdecdly</td><td>Axis acceleration/deceleration delay</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>spcss_srpm</td><td>Spindle speed conversion</td><td>int64/uint64</td><td>-</td><td></td></tr><tr><td>spcss_sspm</td><td>Specified surface speed</td><td>int64/uint64</td><td>-</td><td></td></tr><tr><td>spcss_smax</td><td>Maximum spindle speed for the fixture</td><td>int64/uint64</td><td>-</td><td></td></tr><tr><td>movrlap_input.m</td><td>Input overlapping motion value</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>movrlap_output.m</td><td>Output overlapping motion value</td><td>int64/uint64</td><td>Axis number</td><td></td></tr><tr><td>spload.m</td><td>Spindle load</td><td>int32/uint32</td><td>Spindle number</td><td></td></tr><tr><td>spmaxrpm.m</td><td>Maximum spindle speed ratio</td><td>int32/uint32</td><td>Spindle number</td><td></td></tr><tr><td>spgear.m</td><td>Spindle gear ratio</td><td>int32/uint32</td><td>Spindle number</td><td></td></tr><tr><td>runState</td><td>Operating state</td><td>int32/uint32</td><td>-</td><td>1: Fault 2: Running 3: Idle</td></tr><tr><td>controlMode</td><td>Control mode</td><td>int32/uint32</td><td>-</td><td>0: MID 1: AUTO 3: EDIT 5: JOB 8: INC feed 9: REFERENCE 10: ReMoTe</td></tr><tr><td>programMain</td><td>Machining program name</td><td>string</td><td>-</td><td></td></tr><tr><td>mainProgramNo</td><td>Main program number</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>subProgramNo</td><td>Sub program number</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>alarm</td><td>Alarm ID</td><td>int32/uint32</td><td>-</td><td>0: WS 1: PW 2: IO 3: PS</td></tr><tr><td>alarmMsg.m</td><td>Alarm content</td><td>string</td><td>m as number</td><td>Range is 1-16</td></tr><tr><td>param.m.n</td><td>Parameter</td><td>int32/uint32</td><td>m as parameter ID, n as axis number</td><td>If not axis-related, set to 0</td></tr><tr><td>alarmMsg2.m.n</td><td>Alarm content 2</td><td>int16/uint16/string</td><td>m as number, n as value parameter</td><td>m range is 1-16, n=0 for alarm type, n=1 for alarm number, n=2 for alarm content</td></tr><tr><td>macroType</td><td>Macro variable type</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>macroInfo.m</td><td>Macro variable information</td><td>int16/uint16</td><td>n as value parameter</td><td>n=0 for number of local variables, n=1 for public variable indicator</td></tr><tr><td>macro.m.n</td><td>Macro variable</td><td>int32/uint32</td><td>m as variable ID, n as value parameter</td><td>n=0 for macro variable value, n=1 for decimal places</td></tr><tr><td>paraNum.m</td><td>Number of parameters</td><td>int16/uint16</td><td>n as value parameter</td><td>n=0 for minimum number of parameters, n=1 for maximum number, n=3 for total number of parameters</td></tr><tr><td>pitchInfoNum.m</td><td>Number of pitch error compensation data</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>pitch.m</td><td>Pitch error compensation data</td><td>int8/uint8</td><td>m as number</td><td></td></tr><tr><td>tool.m.n</td><td>Tool data</td><td>int64/uint64</td><td>m as tool number, n as value variable</td><td>m starts from 1, n=0 for tool number, n=1 for usage count, n=2 for tool life, n=3 for rest life</td></tr><tr><td>toolf2.m.n</td><td>Tool data 2</td><td>int64/uint64</td><td>m as tool number, n as value variable</td><td>m starts from 1, n=0 for tool number, n=1 for usage count, n=2 for tool life, n=3 for rest life</td></tr><tr><td>toolOffset.m.n</td><td>Tool offset data</td><td>int32/uint32</td><td>m as tool number, n as tool offset type</td><td>m starts from 1, n is related to the specific system model, e.g., for 0i-D, n=0 for radius wear, n=1 for radius shape, n=2 for length wear, n=3 for length shape</td></tr><tr><td>toolOffsetRange.m.n.l</td><td>Tool offset data setting range</td><td>int32/uint32</td><td>m as tool number, n as tool offset type, l as value type</td><td>m starts from 1, n is related to the specific system model, e.g., for 0i-D, n=0 for radius wear, n=1 for radius shape, n=2 for length wear, n=3 for length shape, l=0 for minimum value, l=1 for maximum value, l=2 for status</td></tr><tr><td>toolOffsetInfo.m</td><td>Tool offset information</td><td>int16/uint16</td><td>m as tool offset type</td><td>m=0 for memory type, m=1 for the number of available tool offsets, n=2 for tool offset types</td></tr><tr><td>wkcdsfms.m</td><td>Workpiece coordinate offset measurement value</td><td>int32/uint32</td><td>Axis number</td><td>Not supported in M series</td></tr><tr><td>wkcdshft.m</td><td>Workpiece coordinate offset value</td><td>int32/uint32</td><td>Axis number</td><td>Not supported in M series</td></tr><tr><td>wksftRange.m</td><td>Workpiece coordinate offset value range</td><td>int32/uint32</td><td>n as value parameter</td><td>m=0 for minimum value, m=1 for maximum value, m=2 for type, Not supported in M series</td></tr><tr><td>zofs</td><td>Workpiece zero offset value</td><td>int32/uint32</td><td>-</td><td></td></tr><tr><td>zofsInfoNum</td><td>Number of workpiece zero offset values</td><td>int32/uint32</td><td>-</td><td></td></tr><tr><td>zofsRange.m.n.l</td><td>Workpiece zero offset setting range</td><td>int32/uint32</td><td>m as workpiece coordinate offset number, n as axis number, l as value type</td><td>l=0 for minimum value, l=1 for maximum value, l=2 for status</td></tr><tr><td>blkCount</td><td>Block counter</td><td>int32/uint32</td><td>-</td><td>-</td></tr><tr><td>execProg</td><td>Currently truly executed program segment</td><td>string</td><td>-</td><td></td></tr><tr><td>mdiPntr</td><td>MDI execution information</td><td>int32/uint32</td><td>-</td><td>Readable only when the device is in MDI mode</td></tr><tr><td>programMainFile</td><td>Main program file information</td><td>string</td><td>-</td><td></td></tr><tr><td>pdfCurDir</td><td>Current program directory</td><td>string</td><td>-</td><td></td></tr><tr><td>pdfDrive.m</td><td>Program device information</td><td>string</td><td>m as device number</td><td>m starts from 1</td></tr><tr><td>progInfo.m</td><td>Program management information</td><td>int32/uint32</td><td>m as value parameter</td><td>m=0 for number of registered programs, m=1 for number of available programs, m=2 for used memory character count, m=3 for unused memory character count</td></tr><tr><td>seqNum</td><td>Program sequence number</td><td>int32/uint32</td><td>-</td><td></td></tr><tr><td>exaxisName.m.n</td><td>Control axis and spindle name</td><td>string</td><td>m as axis type, n as number</td><td>m=0 for control axis, m=1 for spindle, n starts from 1</td></tr><tr><td>axisName.m</td><td>Control axis name</td><td>int8/uint8</td><td>m as number</td><td>m starts from 1</td></tr><tr><td>hndintrpt.m.n</td><td>Handwheel interrupt value</td><td>int32/uint32</td><td>m as type, n as number</td><td>m=0 for input, m=1 for output, n starts from 1</td></tr><tr><td>spdlName.m</td><td>Spindle name</td><td>int8/uint8</td><td>m as number</td><td>m starts from 1</td></tr><tr><td>spmeter.m.n</td><td>Spindle load percentage</td><td>int32/uint32</td><td>m as type, n as number</td><td>m=0 for spindle load, m=1 for spindle motor load, n starts from 1</td></tr><tr><td>svmeter.m</td><td>Servo load percentage</td><td>int32/uint32</td><td>m as number</td><td>m starts from 1</td></tr><tr><td>almhisno</td><td>Number of historical alarms</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>almhistry.m.n</td><td>Historical alarm content</td><td>int16/uint16/string</td><td>m as number, n as value parameter</td><td>m starts from 1, n=0 for alarm type, n=1 for alarm number, n=2 for alarm content</td></tr><tr><td>omhisno</td><td>Number of historical extra operation information</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>omhistry.m.n</td><td>Historical extra operation information content</td><td>int16/uint16/string</td><td>m as number, n as value parameter</td><td>m starts from 1, n=0 for display flag, n=1 for operation information number, n=2 for operation information content</td></tr><tr><td>ophisno</td><td>Number of historical operation information</td><td>int16/uint16</td><td>-</td><td></td></tr><tr><td>ophistry.m.n</td><td>Historical operation information content</td><td>int16/uint16</td><td>m as number, n as value parameter</td><td>m starts from 1</td></tr><tr><td>timer.m.n</td><td>Date and time</td><td>int16/uint16</td><td>m as type, n as value parameter</td><td>m=0 for date, n=0 for year, n=1 for month, n=2 for day; m=1 for time, n=0 for hour, n=1 for minute, n=2 for second</td></tr><tr><td>diagdata.m.n</td><td>Diagnostic data</td><td>int32/uint32</td><td>m as diagnostic number, n as value parameter</td><td>n=0 for diagnostic value, n=1 for decimal point</td></tr><tr><td>opmsg.m.n</td><td>Current operation information</td><td>int16/uint16/string</td><td>m as number, n as value parameter</td><td>m starts from 1, n=0 for type, n=1 for number, n=2 for content</td></tr><tr><td>tlGrpinfo.m.n</td><td>Tool life management information</td><td>int32/uint32</td><td>m as tool group number, n as value parameter</td><td>m starts from 1, n=0 for tool quantity, n=1 for remaining tool quantity, n=2 for tool life, n=3 for tool usage life, n=6 for tool warning life</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>The number of axes starts from 1 and increases according to the actual number of axes. To use the tool life management function, the corresponding parameters on the device must be enabled. If the parameter address does not set <code>@p</code>, it defaults to path 1.</p></div><p><em>CNC address example</em></p><table tabindex="0"><thead><tr><th>address</th><th>description</th></tr></thead><tbody><tr><td>actf</td><td>read actual feed rate</td></tr><tr><td>absolute.1</td><td>read absolute position of no.1 axis</td></tr><tr><td>absolute.1@2</td><td>read absolute position of no.1 axis by path2</td></tr><tr><td>machine.3</td><td>read machine position of no.3 axis</td></tr><tr><td>spload.1</td><td>read load information of no.1 spindle</td></tr><tr><td>spmaxrpm.3</td><td>read maximum r.p.m ratio of no.3 spindle</td></tr><tr><td>param.6712.0</td><td>parts total</td></tr><tr><td>param.6711.0</td><td>parts count</td></tr><tr><td>param.6750.0</td><td>power on time</td></tr><tr><td>param.6753.0</td><td>cutting on time</td></tr><tr><td>macro.3142.0</td><td>Value of macro variable #3142</td></tr><tr><td>tool.1.0</td><td>Tool number 1</td></tr><tr><td>tool.1.1</td><td>Usage count of tool number 1</td></tr><tr><td>tool.1.2</td><td>Total life of tool number 1</td></tr><tr><td>tool.1.3</td><td>Warning life of tool number 1</td></tr><tr><td>diagdata.1333.3</td><td>Data of diagnostic number 1333</td></tr><tr><td>tlGrpinfo.1.1</td><td>Remaining tool life of tool life group 1</td></tr><tr><td>toolOffset.1.2</td><td>Length wear of tool number 1</td></tr></tbody></table><h2 id="pmc-data" tabindex="-1">PMC Data <a class="header-anchor" href="#pmc-data" aria-label="Permalink to “PMC Data”">​</a></h2><p>Address Format <code>AREA ADDRESS[.BIT][.LEN][@p]</code></p><table tabindex="0"><thead><tr><th>tag address</th><th>description</th><th>data type</th><th>access</th></tr></thead><tbody><tr><td>A</td><td>message demand</td><td>all</td><td>read/write</td></tr><tr><td>C</td><td>counter</td><td>all</td><td>read/write</td></tr><tr><td>D</td><td>data table</td><td>all</td><td>read/write</td></tr><tr><td>E</td><td>extended relay</td><td>all</td><td>read/write</td></tr><tr><td>F</td><td>signal to CNC -&gt; PMC</td><td>all</td><td>read</td></tr><tr><td>G</td><td>signal to PMC -&gt; CNC</td><td>all</td><td>read/write</td></tr><tr><td>K</td><td>keep relay</td><td>all</td><td>read/write</td></tr><tr><td>M</td><td>input signal from other device</td><td>all</td><td>read/write</td></tr><tr><td>N</td><td>output signal from other device</td><td>all</td><td>read/write</td></tr><tr><td>R</td><td>internal relay</td><td>all</td><td>read/write</td></tr><tr><td>T</td><td>changeable timer</td><td>all</td><td>read/write</td></tr><tr><td>X</td><td>signal to machine -&gt; PMC</td><td>all</td><td>read</td></tr><tr><td>Y</td><td>signal to PMC -&gt; machine</td><td>all</td><td>read/write</td></tr></tbody></table><p><em>PMC address example</em></p><table tabindex="0"><thead><tr><th>address</th><th>data type</th><th>descrption</th></tr></thead><tbody><tr><td>A0</td><td>uint8/int8/uint16/int16/uint32/int32/int64/uint64/float/double</td><td>PMC <strong>message demand</strong>，address 0</td></tr><tr><td>A0.1</td><td>bit</td><td>PMC <strong>message demand</strong> ，no.1 bit of address 0</td></tr><tr><td>A0.0</td><td>bit</td><td>PMC <strong>message demand</strong> ，no.0 bit of address 0</td></tr><tr><td>A0.2</td><td>string</td><td>PMC <strong>message demand</strong> ，address 0 starts with a string of length 2</td></tr><tr><td>D0.2</td><td>string</td><td>PMC <strong>data table</strong> ，address 0 starts with a string of length 2</td></tr><tr><td>D0.7</td><td>bit</td><td>PMC <strong>data table</strong> ，no.7 bit of address 0</td></tr><tr><td>G12</td><td>uint8</td><td>PMC <strong>signal to PMC -&gt; CNC</strong> ，address 12, 255-(G12)，feedrate overriden</td></tr><tr><td>G30</td><td>uint16</td><td>PMC <strong>signal to PMC -&gt; CNC</strong> ，address 30, spindle overriden</td></tr></tbody></table>',20)])])}const c=d(n,[["render",o]]);export{p as __pageData,c as default};
