import{_ as d,c as e,o as r,ah as a}from"./chunks/framework.DGOuNAW6.js";const p=JSON.parse('{"title":"KND CNC","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/knd/knd.md","filePath":"en_US/configuration/south-devices/knd/knd.md"}'),o={name:"en_US/configuration/south-devices/knd/knd.md"};function i(s,t,n,l,m,c){return r(),e("div",null,[...t[0]||(t[0]=[a('<h1 id="knd-cnc" tabindex="-1">KND CNC <a class="header-anchor" href="#knd-cnc" aria-label="Permalink to “KND CNC”">​</a></h1><p>The KND CNC driver accesses KND K2000, K1000 C/Ci/F/Fi, and K1000TTCi series CNC systems via HTTP protocol, enabling real-time collection of various device operation data, including program name, spindle override, operating status, PLC points, etc.</p><h2 id="device-settings" tabindex="-1">Device Settings <a class="header-anchor" href="#device-settings" aria-label="Permalink to “Device Settings”">​</a></h2><table tabindex="0"><thead><tr><th>Field</th><th>Description</th></tr></thead><tbody><tr><td>host</td><td>Device IP address</td></tr><tr><td>port</td><td>Device port number, default 8000</td></tr></tbody></table><h2 id="supported-data-types" tabindex="-1">Supported Data Types <a class="header-anchor" href="#supported-data-types" aria-label="Permalink to “Supported Data Types”">​</a></h2><ul><li>uint8</li><li>int8</li><li>uint16</li><li>int16</li><li>uint32</li><li>int32</li><li>uint64</li><li>int64</li><li>float</li><li>double</li><li>bool</li><li>bit</li><li>string</li></ul><h2 id="cnc-data" tabindex="-1">CNC Data <a class="header-anchor" href="#cnc-data" aria-label="Permalink to “CNC Data”">​</a></h2><blockquote><p>address[.m]</p></blockquote><table tabindex="0"><thead><tr><th>Tag Identifier (Address)</th><th>Description</th><th>Data Type</th><th>Parameter</th><th>Remarks</th></tr></thead><tbody><tr><td>systemInfo.id</td><td>ID</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>systemInfo.type</td><td>System Type</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.manufacturer</td><td>Manufacturer</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.manufacture-time</td><td>Manufacture Time</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.soft-version</td><td>System Software Version</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.fpga-version</td><td>FPGA Version</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.ladder-version</td><td>Ladder Version</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.user-axes</td><td>User Axis List</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.run-status</td><td>Current Run Status</td><td>int32</td><td>-</td><td>0: CNC is stopped 1: CNC is paused (feed hold) 2: CNC is running</td></tr><tr><td>systemStatus.opr-mode</td><td>Current Operation Mode</td><td>int32</td><td>-</td><td>0: Manual data input mode 1: Automatic mode 2: Invalid mode 3: Edit mode 4: Single step mode 5: Manual mode 8: Handle wheel mode 9: Machine zero return mode 10: Program zero return mode</td></tr><tr><td>systemStatus.ready</td><td>Is Ready</td><td>bool</td><td>-</td><td>-</td></tr><tr><td>systemStatus.not-ready-reason</td><td>Not Ready Reason Mask</td><td>int32</td><td>-</td><td>0x1: Emergency stop signal active 0x2: Servo not ready 0x4: IO not ready (remote IO devices, etc.)</td></tr><tr><td>systemStatus.alarms</td><td>Alarm List</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>alarms</td><td>Alarm Description Information</td><td>string</td><td>m</td><td>prm-switch: Parameter switch alarm (system parameter switch or servo parameter switch) reboot: Power on/off alarm plc: PLC alarm or prompt (external alarm) ps: PS alarm (operation error) over-travel: Over-travel alarm over-heat: Overheat alarm mem: Memory alarm servo: Servo drive alarm servo-bus: Servo bus alarm over-workarea: Out of work area alarm io-bus: IO bus alarm io-module: IO module alarm manufacture: Machine factory alarm forbid-move: Axis movement not allowed when axis moves</td></tr><tr><td>absolute</td><td>Absolute Coordinates</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>machine</td><td>Machine Coordinates</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>relative</td><td>Relative Coordinates</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>cycleTime</td><td>Processing Time</td><td>int32</td><td>m</td><td>total: Processing time (unit: seconds) cur: Cycle time (unit: seconds)</td></tr><tr><td>workCounts</td><td>Processing Counts</td><td>int32</td><td>m</td><td>total: Total processing count batch: Single batch processing count</td></tr><tr><td>workCountGoals</td><td>Target Counts</td><td>int32</td><td>m</td><td>total: Total target count batch: Single batch target count</td></tr><tr><td>feedOverride</td><td>Current Feed Override</td><td>double</td><td>-</td><td>-</td></tr><tr><td>jogOverride</td><td>Current Jog Override</td><td>double</td><td>-</td><td>-</td></tr><tr><td>rapidOverride</td><td>Current Rapid Override</td><td>double</td><td>-</td><td>-</td></tr><tr><td>handleOverride</td><td>Current Handle/Single Step Override</td><td>double</td><td>-</td><td>-</td></tr><tr><td>spindleOverride</td><td>Current Spindle Override</td><td>double</td><td>m</td><td>1: Spindle 1 2: Spindle 2 3: Spindle 3</td></tr><tr><td>spSpeed</td><td>Current Spindle Speed</td><td>double</td><td>m</td><td>1: Spindle 1 2: Spindle 2 3: Spindle 3</td></tr><tr><td>feedrate</td><td>Actual Feedrate</td><td>double</td><td>-</td><td>-</td></tr><tr><td>g54</td><td>G54 Work Coordinate System</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>g55</td><td>G55 Work Coordinate System</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>g56</td><td>G56 Work Coordinate System</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>g57</td><td>G57 Work Coordinate System</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>g58</td><td>G58 Work Coordinate System</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>g59</td><td>G59 Work Coordinate System</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>workCoorsCur</td><td>Current Work Coordinate System</td><td>string</td><td>-</td><td>-</td></tr><tr><td>vars</td><td>Macro Variables</td><td>double</td><td>m</td><td>Macro variable number</td></tr><tr><td>progCur</td><td>Current Program</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>progExecStatus</td><td>Program Execution Status</td><td>int32</td><td>m</td><td>O: Program O number N: Program N number P: Paragraph number</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>Spindle numbers start from 1 and increase according to the actual number of spindles.</p><p>Macro variables (vars) are readable and writable, others are read-only.</p></div><p><em>CNC Address Examples</em></p><table tabindex="0"><thead><tr><th>Address</th><th>Description</th></tr></thead><tbody><tr><td>systemInfo.type</td><td>Read processing main program number</td></tr><tr><td>machine.X</td><td>Read X-axis coordinate</td></tr><tr><td>vars.100</td><td>Read/write macro variable 100</td></tr><tr><td>feedOverride</td><td>Read current feed override</td></tr><tr><td>alarms.plc</td><td>PLC alarm or prompt (external alarm)</td></tr><tr><td>spindleOverride.1</td><td>Spindle 1 override</td></tr><tr><td>spSpeed.1</td><td>Spindle 1 speed</td></tr><tr><td>cycleTime.cur</td><td>Cycle time</td></tr></tbody></table><h3 id="plc-data" tabindex="-1">PLC Data <a class="header-anchor" href="#plc-data" aria-label="Permalink to “PLC Data”">​</a></h3><h3 id="address-format" tabindex="-1">Address Format <a class="header-anchor" href="#address-format" aria-label="Permalink to “Address Format”">​</a></h3><blockquote><p>AREA ADDRESS[.BIT][.LEN]</p></blockquote><table tabindex="0"><thead><tr><th>Identifier</th><th>Description</th><th>Type</th><th>Permission</th></tr></thead><tbody><tr><td>X</td><td>DI Input</td><td>all</td><td>Read</td></tr><tr><td>Y</td><td>DO Output</td><td>all</td><td>Read</td></tr><tr><td>F</td><td>NC -&gt; PLC</td><td>all</td><td>Read</td></tr><tr><td>G</td><td>PLC -&gt; NC</td><td>all</td><td>Read</td></tr><tr><td>R</td><td>PLC Internal Control Relay</td><td>all</td><td>Read/Write</td></tr><tr><td>S</td><td>PLC Internal Special Flag</td><td>all</td><td>Read</td></tr><tr><td>K</td><td>PLC Internal Power-On Relay</td><td>all</td><td>Read</td></tr><tr><td>D</td><td>Data Table</td><td>all</td><td>Read</td></tr><tr><td>TL</td><td>Label Sequence Number</td><td>all</td><td>Read</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>Currently, only part of the R area can be set, i.e., R17000-R17099, and it requires explicit permission in the ladder diagram. The ladder diagram must set G138 to 181 to allow remote modification of the above R area.</p></div><blockquote><p>AREA ADDRESS[.cur/conf]</p></blockquote><table tabindex="0"><thead><tr><th>Identifier</th><th>Description</th><th>Type</th><th>Permission</th></tr></thead><tbody><tr><td>T</td><td>Timer</td><td>int32/uint32</td><td>Read</td></tr><tr><td>C</td><td>Counter</td><td>int32/uint32</td><td>Read</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>When reading timers and counters, you need to specify whether it is the set value or the current value.</p></div><p><em>Common PLC Points</em></p><table tabindex="0"><thead><tr><th>Address</th><th>Type</th><th>Description</th></tr></thead><tbody><tr><td>X0.0</td><td>bit</td><td>DI area, data at address 0</td></tr><tr><td>Y0.0</td><td>bit</td><td>DO area, data at address 0</td></tr><tr><td>D10</td><td>int32</td><td>Data table area, data at address 10</td></tr><tr><td>R17000</td><td>float</td><td>Internal control relay area, data at address 17000</td></tr><tr><td>D20.24</td><td>string</td><td>Data table area, data at address 20</td></tr><tr><td>T0.conf</td><td>int32</td><td>Timer area, set value data at address 0</td></tr><tr><td>T0.cur</td><td>int32</td><td>Timer area, current data at address 0</td></tr></tbody></table>',22)])])}const h=d(o,[["render",i]]);export{p as __pageData,h as default};
