import{_ as d,c as e,o as a,ah as r}from"./chunks/framework.D2dsaVOY.js";const h=JSON.parse('{"title":"Overview","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/mitsubishi-cnc/mitsubishi-cnc.md","filePath":"en_US/configuration/south-devices/mitsubishi-cnc/mitsubishi-cnc.md"}'),i={name:"en_US/configuration/south-devices/mitsubishi-cnc/mitsubishi-cnc.md"};function s(n,t,o,l,c,p){return a(),e("div",null,[...t[0]||(t[0]=[r('<h1 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to “Overview”">​</a></h1><p>The Mitsubishi CNC driver can access Mitsubishi M70 and M80 series machine tools and machining centers through TCP/IP protocol, and collect various CNC operation data in real time, including program name, spindle speed, spindle speed override, spindle load, emergency stop, etc.</p><h2 id="parameter-configuration" tabindex="-1">Parameter Configuration <a class="header-anchor" href="#parameter-configuration" aria-label="Permalink to “Parameter Configuration”">​</a></h2><table tabindex="0"><thead><tr><th>Parameter</th><th>Description</th></tr></thead><tbody><tr><td>host</td><td>device ip address</td></tr><tr><td>port</td><td>device port, default 683</td></tr></tbody></table><h2 id="support-data-type" tabindex="-1">Support Data Type <a class="header-anchor" href="#support-data-type" aria-label="Permalink to “Support Data Type”">​</a></h2><ul><li>uint16</li><li>int16</li><li>uint32</li><li>int32</li><li>bit</li><li>string</li></ul><h2 id="cnc-data" tabindex="-1">CNC Data <a class="header-anchor" href="#cnc-data" aria-label="Permalink to “CNC Data”">​</a></h2><table tabindex="0"><thead><tr><th>tag address</th><th>description</th><th>data type</th><th>parameter</th></tr></thead><tbody><tr><td>ProgramMain</td><td>main program no</td><td>string</td><td>-</td></tr><tr><td>FeedSpeedFA</td><td>F command feed speed</td><td>double</td><td>-</td></tr><tr><td>FeedSpeedFM</td><td>Manual effective feed speed</td><td>double</td><td>-</td></tr><tr><td>FeedSpeedFS</td><td>Synchronization feed speed</td><td>double</td><td>-</td></tr><tr><td>FeedSpeedFC</td><td>Automatic effective feed speed</td><td>double</td><td>-</td></tr><tr><td>FeedSpeedFE</td><td>Screw lead</td><td>double</td><td>-</td></tr></tbody></table><p><em>CNC address example</em></p><table tabindex="0"><thead><tr><th>address</th><th>description</th></tr></thead><tbody><tr><td>ProgramMain</td><td>read main program no</td></tr></tbody></table><h3 id="plc-data" tabindex="-1">PLC Data <a class="header-anchor" href="#plc-data" aria-label="Permalink to “PLC Data”">​</a></h3><table tabindex="0"><thead><tr><th>tag address</th><th>description</th><th>data type</th><th>access</th></tr></thead><tbody><tr><td>X</td><td>input signal to programmable controller</td><td>bit/16bit/32bit</td><td>rw</td></tr><tr><td>Y</td><td>output signal to programmable controller</td><td>bit/16bit/32bit</td><td>rw</td></tr><tr><td>R</td><td>file register</td><td>16bit/32bit</td><td>rw</td></tr></tbody></table><p><em>PLC Common Address</em></p><table tabindex="0"><thead><tr><th>address</th><th>data type</th><th>descrption</th></tr></thead><tbody><tr><td>R69</td><td>uint16</td><td>PLC R area, address 69,emergency stop（65519 ON 65535 OFF）</td></tr><tr><td>R2500</td><td>uint16</td><td>PLC R area, address 2500, feed rate</td></tr><tr><td>R7008</td><td>uint16</td><td>PLC R area, address 7008, spindle rate</td></tr><tr><td>R6506</td><td>uint32</td><td>PLC R area, address 6506, actual spindle speed</td></tr><tr><td>R7000</td><td>uint32</td><td>PLC R area, address 7000~7001, S commond spindle speed</td></tr><tr><td>R6525</td><td>uint16</td><td>PLC R area, address 6525, spindle load</td></tr><tr><td>R6529</td><td>uint16</td><td>PLC R area, address 6529, spindle alarm no</td></tr><tr><td>R606</td><td>uint32</td><td>PLC R area, address 606 ~ 607 , current value of workpiece processing number</td></tr><tr><td>R608</td><td>uint32</td><td>PLC R area, address 608 ~ 609 , maximum value of workpiece processing number</td></tr><tr><td>R11824</td><td>uint32</td><td>PLC R area, address 11824 ~ 11825 , tool group number in use</td></tr><tr><td>R11826</td><td>uint32</td><td>PLC R area, address 11826 ~ 11827 , tool number in use</td></tr><tr><td>R11830</td><td>uint32</td><td>PLC R area, address 11830 ~ 11831 , cumulative data on tool usage time in use</td></tr><tr><td>R11832</td><td>uint32</td><td>PLC R area, address 11832 ~ 11833 , data on tool life setting time in use</td></tr><tr><td>XC00</td><td>uint16</td><td>PLC X area, address C00 ~ C0F , control mode (1 JOG 2 HO 4 SO 8 16 PTPO 32 ASTO 256 MEMO 2048 DO)</td></tr><tr><td>XC12</td><td>bit</td><td>PLC X area, address C12 , auto running</td></tr><tr><td>XC13</td><td>bit</td><td>PLC X area, address C13 , auto running start</td></tr><tr><td>XC14</td><td>bit</td><td>PLC X area, address C14 , auto running pause</td></tr><tr><td>XC15</td><td>bit</td><td>PLC X area, address C15 , reset</td></tr><tr><td>XC20</td><td>bit</td><td>PLC X area, address C20 , fast feed</td></tr><tr><td>XC21</td><td>bit</td><td>PLC X area, address C21 , machining feed</td></tr><tr><td>XC22</td><td>bit</td><td>PLC X area, address C22 , tapping</td></tr><tr><td>XC23</td><td>bit</td><td>PLC X area, address C23 , thread cutting</td></tr><tr><td>XC24</td><td>bit</td><td>PLC X area, address C24 , synchronous feed</td></tr><tr><td>XC25</td><td>bit</td><td>PLC X area, address C25 , constant speed</td></tr><tr><td>XC26</td><td>bit</td><td>PLC X area, address C26 , jumping</td></tr><tr><td>XC27</td><td>bit</td><td>PLC X area, address C27 , reference point return in progress</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>The PLC data sheet can be found on Mitsubishi&#39;s official website provided in the &quot;PLC Development Manual - M800/M80/E80 Series&quot;, which contains more information on system operation data.</p></div>',15)])])}const b=d(i,[["render",s]]);export{h as __pageData,b as default};
