import{_ as a,c as t,o,ah as i}from"./chunks/framework.D2dsaVOY.js";const p=JSON.parse('{"title":"Modbus TCP FAQ","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/modbus-tcp/faq/faq.md","filePath":"en_US/configuration/south-devices/modbus-tcp/faq/faq.md"}'),r={name:"en_US/configuration/south-devices/modbus-tcp/faq/faq.md"};function d(l,e,n,s,c,u){return o(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="modbus-tcp-faq" tabindex="-1">Modbus TCP FAQ <a class="header-anchor" href="#modbus-tcp-faq" aria-label="Permalink to “Modbus TCP FAQ”">​</a></h1><h2 id="unable-to-read-tag-value-error-code-3002" tabindex="-1">Unable to Read Tag Value, Error Code 3002 <a class="header-anchor" href="#unable-to-read-tag-value-error-code-3002" aria-label="Permalink to “Unable to Read Tag Value, Error Code 3002”">​</a></h2><hr><ul><li>Turn on DEBUG logs, check the logs, data flow, and whether the device has responded with correct data (if multiple upstream devices are collecting data from the same serial device, exceptions may occur).</li><li>Check if the station number is correct.</li><li>Check if the address is correct (GridBeat Modbus address starts from 1).</li><li>Some devices may not support reading only one point at a time, so all points need to be configured (GridBeat will automatically optimize batch reading).</li><li>Check if the connection is valid. Use the telnet command to detect if there are any abnormalities in the link.</li><li>The device supports the Modbus RTU protocol, not Modbus TCP. Try using the Modbus RTU plugin.</li></ul><h2 id="tag-value-invalid-error-code-3008" tabindex="-1">Tag value Invalid, Error Code 3008 <a class="header-anchor" href="#tag-value-invalid-error-code-3008" aria-label="Permalink to “Tag value Invalid, Error Code 3008”">​</a></h2><hr><ul><li>The device is offline and the data cannot be updated.</li><li>The device does not respond within a certain period of time.</li><li>The device supports the Modbus RTU protocol, not Modbus TCP. Try using the Modbus RTU plugin.</li></ul><h2 id="tag-value-read-does-not-match-expectations" tabindex="-1">Tag Value Read does not Match Expectations <a class="header-anchor" href="#tag-value-read-does-not-match-expectations" aria-label="Permalink to “Tag Value Read does not Match Expectations”">​</a></h2><hr><ul><li>Incorrect data type.</li><li>Incorrect byte order.</li><li>Presence of multiple upstream masters collecting data results in data corruption.</li><li>The DTU has enabled registration or heartbeat packets, causing GridBeat to consider data flow data as incorrect.</li><li>The device actively uploads data, but the standard Modbus only supports requesting and then responding with corresponding data.</li><li>The device supports Modbus RTU protocol instead of Modbus TCP. Try using the Modbus RTU plugin.</li></ul>',10)])])}const b=a(r,[["render",d]]);export{p as __pageData,b as default};
