import{_ as d,c as e,o as r,ah as a}from"./chunks/framework.DGOuNAW6.js";const c=JSON.parse('{"title":"MITSUBISHI CNC","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/neuhub/mitsubishi-cnc.md","filePath":"en_US/configuration/south-devices/neuhub/mitsubishi-cnc.md"}'),i={name:"en_US/configuration/south-devices/neuhub/mitsubishi-cnc.md"};function s(n,t,o,l,m,u){return r(),e("div",null,[...t[0]||(t[0]=[a('<h1 id="mitsubishi-cnc" tabindex="-1">MITSUBISHI CNC <a class="header-anchor" href="#mitsubishi-cnc" aria-label="Permalink to “MITSUBISHI CNC”">​</a></h1><p>GridBeat can use the GridBeat HUB driver and GridBeatHUB Windows program to indirectly access Mitsubishi M70/M80 CNC systems, enabling real-time collection of various device operation data, including program names, feed speed, operation status, power consumption, PLC points, global variables, parameters, etc.</p><h2 id="neuron-hub-windows-program-parameters" tabindex="-1">NEURON HUB Windows Program Parameters <a class="header-anchor" href="#neuron-hub-windows-program-parameters" aria-label="Permalink to “NEURON HUB Windows Program Parameters”">​</a></h2><table tabindex="0"><thead><tr><th>Parameter</th><th>Description</th></tr></thead><tbody><tr><td>Node Name</td><td>Node name, must be unique to distinguish multiple nodes</td></tr><tr><td>Host</td><td>IP address of the CNC device to connect to</td></tr><tr><td>cnctype</td><td>Device type (supported: M700L, M700M, M800L, M800M)</td></tr><tr><td>cardno</td><td>Control card number (default: 1)</td></tr></tbody></table><h2 id="supported-data-types" tabindex="-1">Supported Data Types <a class="header-anchor" href="#supported-data-types" aria-label="Permalink to “Supported Data Types”">​</a></h2><ul><li>uint8</li><li>int8</li><li>uint32</li><li>int32</li><li>uint64</li><li>int64</li><li>float</li><li>double</li><li>bit</li><li>string</li><li>ARRAY_STRING</li><li>ARRAY_DOUBLE</li></ul><h2 id="cnc-data" tabindex="-1">CNC Data <a class="header-anchor" href="#cnc-data" aria-label="Permalink to “CNC Data”">​</a></h2><blockquote><p>address[.m][.n][.k][.j]</p></blockquote><table tabindex="0"><thead><tr><th>Tag Identifier (Address)</th><th>Description</th><th>Data Type</th><th>Parameters</th><th>Remarks</th></tr></thead><tbody><tr><td>systemStatus</td><td>Current operation status</td><td>int32</td><td>m</td><td>m: 0 Tool setting status 1 Auto status 2 Auto running status 3 Auto paused status</td></tr><tr><td>spindleInfo</td><td>Spindle status</td><td>int64</td><td>m n</td><td>m: Spindle number n: 0 Gain 1: Position deviation 2: Motor speed 3: Load 5: Alarm1 6: Alarm2 7: Alarm3 8: Alarm4</td></tr><tr><td>servoInfo</td><td>Servo axis status</td><td>int64</td><td>m n</td><td>m: Axis number n: 0 Gain 1: Position deviation 2: Motor speed 3: Current 6: Load</td></tr><tr><td>work</td><td>Workpiece coordinates</td><td>double</td><td>m</td><td>m: Axis number</td></tr><tr><td>machine</td><td>Machine coordinates</td><td>double</td><td>m</td><td>m: Axis number</td></tr><tr><td>relative</td><td>Relative coordinates</td><td>double</td><td>m</td><td>m: Axis number</td></tr><tr><td>distance</td><td>Remaining distance</td><td>double</td><td>m</td><td>m: Axis number</td></tr><tr><td>feedSpeed</td><td>Feed speed</td><td>double</td><td>m</td><td>0 FA 1 FM 2 FS 3 Fc 4 FE</td></tr><tr><td>param</td><td>Parameters</td><td>ARRAY_STRING</td><td>m n k j</td><td>m: Axis number n: Group number k: Parameter number j: Parameter count</td></tr><tr><td>toolOffset</td><td>Tool compensation</td><td>double</td><td>m n k</td><td>m: Type n: Tool compensation type k: Number</td></tr><tr><td>alarm</td><td>Alarms</td><td>ARRAY_STRING</td><td>-</td><td>-</td></tr><tr><td>runTime</td><td>Auto run time</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>startTime</td><td>Auto start time</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>aliveTime</td><td>Power-on time</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>estimateTime</td><td>External integration time</td><td>int32</td><td>m</td><td>1: Timer1 2: Timer2</td></tr><tr><td>commonVar</td><td>Global variables</td><td>double</td><td>m</td><td>Variable number</td></tr><tr><td>localVar</td><td>Local variables</td><td>double</td><td>m n</td><td>m: Variable number n: Level</td></tr><tr><td>invalidStatus</td><td>Invalid status</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>commandStatus</td><td>Command status</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>cuttingMode</td><td>Cutting mode</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>mainProgram</td><td>Main program</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>subProgram</td><td>Subprogram</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>mainSeqNum</td><td>Main sequence number</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>subSeqNum</td><td>Sub sequence number</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>programCurrentBlock</td><td>Current program block</td><td>string</td><td>-</td><td>-</td></tr><tr><td>powerConsumption</td><td>Power consumption</td><td>ARRAY_DOUBLE</td><td>m</td><td>m: Axis number [0]: Total system power [1]: Servo power [3]: Spindle power</td></tr><tr><td>toolLife</td><td>Tool life</td><td>ARRAY_STRING</td><td>m n</td><td>m: Group number n: Tool number</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>commonVar is readable/writable; others are read-only.</p></div><p><em>CNC Address Examples</em></p><table tabindex="0"><thead><tr><th>Address</th><th>Description</th></tr></thead><tbody><tr><td>systemStatus.0</td><td>Tool setting status</td></tr><tr><td>machine.1</td><td>Read axis 1 coordinate</td></tr><tr><td>commonVar.100</td><td>Read/write global variable 100</td></tr><tr><td>feedSpeed.0</td><td>Read current feed speed</td></tr><tr><td>alarm</td><td>Current alarm list</td></tr><tr><td>param.1.30.8002.1</td><td>Part count</td></tr></tbody></table><h3 id="plc-data" tabindex="-1">PLC Data <a class="header-anchor" href="#plc-data" aria-label="Permalink to “PLC Data”">​</a></h3><table tabindex="0"><thead><tr><th>Identifier</th><th>Description</th><th>Type</th><th>Permission</th></tr></thead><tbody><tr><td>B</td><td>Counters (fixed counters)</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>C</td><td>Counter coils</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>D</td><td>Data registers</td><td>16bit/32bit</td><td>Read/Write</td></tr><tr><td>E</td><td>Special relays</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>F</td><td>Alarm message temporary memory</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>G</td><td>Temporary memory</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>I</td><td>Devices</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>J</td><td>J devices</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>L</td><td>Latch relays (backup memory)</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>M</td><td>Temporary memory</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>Q</td><td>Q devices</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>R</td><td>File registers</td><td>16bit/32bit</td><td>Read/Write</td></tr><tr><td>SM</td><td>Special relays (for linking)</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>SD</td><td>Special registers</td><td>16bit/32bit</td><td>Read/Write</td></tr><tr><td>ST</td><td>Accumulative timers</td><td>16bit/32bit</td><td>Read/Write</td></tr><tr><td>SW</td><td>Special registers (for linking)</td><td>16bit/32bit</td><td>Read/Write</td></tr><tr><td>T</td><td>10ms timer units</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>U</td><td>Input signals to PLC</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>V</td><td>V devices</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>W</td><td>Input signals to PLC</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>X</td><td>Input signals</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>Y</td><td>Output signals</td><td>bit/16bit/32bit</td><td>Read/Write</td></tr><tr><td>ZR</td><td>File registers</td><td>16bit/32bit</td><td>Read/Write</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>Addresses should be entered in hexadecimal format.</p></div><p><em>Common PLC Addresses</em></p><table tabindex="0"><thead><tr><th>Address</th><th>Type</th><th>Description</th></tr></thead><tbody><tr><td>R69</td><td>uint16</td><td>PLC R area, address 69 data (EMG emergency stop flag: 65519 ON, 65535 OFF)</td></tr><tr><td>R2500</td><td>uint16</td><td>PLC R area, address 2500 data (feed override)</td></tr><tr><td>R7008</td><td>uint16</td><td>PLC R area, address 7008 data (spindle override)</td></tr><tr><td>R6506</td><td>uint32</td><td>PLC R area, address 6506 data (actual spindle speed)</td></tr><tr><td>R7000</td><td>uint32</td><td>PLC R area, address 7000~7001 data (command spindle speed)</td></tr><tr><td>R6525</td><td>uint16</td><td>PLC R area, address 6525 data (spindle load)</td></tr><tr><td>R6529</td><td>uint16</td><td>PLC R area, address 6529 data (spindle alarm number)</td></tr><tr><td>R606</td><td>uint32</td><td>PLC R area, address 606~607 data (current part count)</td></tr><tr><td>R608</td><td>uint32</td><td>PLC R area, address 608~609 data (maximum part count)</td></tr><tr><td>R11824</td><td>uint32</td><td>PLC R area, address 11824~11825 data (currently used tool group number)</td></tr><tr><td>R11826</td><td>uint32</td><td>PLC R area, address 11826~11827 data (currently used tool number)</td></tr><tr><td>R11830</td><td>uint32</td><td>PLC R area, address 11830~11831 data (currently used tool cumulative usage time)</td></tr><tr><td>R11832</td><td>uint32</td><td>PLC R area, address 11832~11833 data (currently used tool life setting time)</td></tr><tr><td>XC00</td><td>uint16</td><td>PLC X area, address C00~C0F data (control mode: 1 JOG mode 2 Handwheel mode 4 Increment mode 8 Manual feed mode 16 Reference return mode 32 Auto initial setting mode 256 Memory mode 2048 MDI mode)</td></tr><tr><td>XC12</td><td>bit</td><td>PLC X area, address C12 data (auto running)</td></tr><tr><td>XC13</td><td>bit</td><td>PLC X area, address C13 data (auto running start)</td></tr><tr><td>XC14</td><td>bit</td><td>PLC X area, address C14 data (auto running pause)</td></tr><tr><td>XC15</td><td>bit</td><td>PLC X area, address C15 data (reset)</td></tr><tr><td>XC20</td><td>bit</td><td>PLC X area, address C20 data (rapid feed)</td></tr><tr><td>XC21</td><td>bit</td><td>PLC X area, address C21 data (cutting feed)</td></tr><tr><td>XC22</td><td>bit</td><td>PLC X area, address C22 data (tapping)</td></tr><tr><td>XC23</td><td>bit</td><td>PLC X area, address C23 data (thread cutting)</td></tr><tr><td>XC24</td><td>bit</td><td>PLC X area, address C24 data (synchronous feed)</td></tr><tr><td>XC25</td><td>bit</td><td>PLC X area, address C25 data (constant speed)</td></tr><tr><td>XC26</td><td>bit</td><td>PLC X area, address C26 data (jump)</td></tr><tr><td>XC27</td><td>bit</td><td>PLC X area, address C27 data (reference return)</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>For more PLC data, refer to Mitsubishi&#39;s official &quot;PLC Development Manual - M800/M80/E80 Series&quot; for additional system operation data.</p></div>',18)])])}const p=d(i,[["render",s]]);export{c as __pageData,p as default};
