import{_ as d,c as e,o as r,ah as a}from"./chunks/framework.DGOuNAW6.js";const p=JSON.parse('{"title":"SYNTEC CNC","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/neuhub/syntec-cnc.md","filePath":"en_US/configuration/south-devices/neuhub/syntec-cnc.md"}'),i={name:"en_US/configuration/south-devices/neuhub/syntec-cnc.md"};function s(o,t,n,l,c,m){return r(),e("div",null,[...t[0]||(t[0]=[a('<h1 id="syntec-cnc" tabindex="-1">SYNTEC CNC <a class="header-anchor" href="#syntec-cnc" aria-label="Permalink to “SYNTEC CNC”">​</a></h1><p>GridBeat can use the GridBeat HUB driver and GridBeatHUB Windows program to indirectly access the SYNTEC CNC system, enabling real-time collection of various device operation data, including program names, spindle override, operation status, PLC points, parameters, global variables, etc.</p><h2 id="neuron-hub-windows-program-parameters" tabindex="-1">NEURON HUB Windows Program Parameters <a class="header-anchor" href="#neuron-hub-windows-program-parameters" aria-label="Permalink to “NEURON HUB Windows Program Parameters”">​</a></h2><table tabindex="0"><thead><tr><th>Parameter</th><th>Description</th></tr></thead><tbody><tr><td>Node Name</td><td>Node name, must be unique to distinguish multiple nodes</td></tr><tr><td>Host</td><td>IP address of the CNC device to connect to</td></tr></tbody></table><h2 id="supported-data-types" tabindex="-1">Supported Data Types <a class="header-anchor" href="#supported-data-types" aria-label="Permalink to “Supported Data Types”">​</a></h2><ul><li>uint8</li><li>int8</li><li>uint32</li><li>int32</li><li>uint64</li><li>int64</li><li>float</li><li>double</li><li>bit</li><li>string</li><li>ARRAY_STRING</li></ul><h2 id="cnc-data" tabindex="-1">CNC Data <a class="header-anchor" href="#cnc-data" aria-label="Permalink to “CNC Data”">​</a></h2><blockquote><p>address[.m][.n]</p></blockquote><table tabindex="0"><thead><tr><th>Tag Identifier (Address)</th><th>Description</th><th>Data Type</th><th>Parameters</th><th>Remarks</th></tr></thead><tbody><tr><td>systemInfo.axes</td><td>Controllable axes count</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>systemInfo.cnc_type</td><td>System type</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.max_axes</td><td>Maximum axes count</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>systemInfo.series</td><td>M/T type</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.nc_ver</td><td>System software version</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.axis_name</td><td>Axis coordinate names</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.status</td><td>Current operation status</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.main_prog</td><td>Main program name</td><td>string</td><td>-</td><td>0: Input mode 1: Auto mode 2: Invalid mode 3: Edit mode 4: Step mode 5: Manual mode 8: Handwheel mode 9: Mechanical homing mode 10: Program homing mode</td></tr><tr><td>systemStatus.cur_prog</td><td>Currently executing program name</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.mode</td><td>Mode</td><td>string</td><td>-</td><td>0x1: Emergency stop signal active 0x2: Servo not ready 0x4: IO not ready (remote IO devices, etc.)</td></tr><tr><td>systemStatus.alarm</td><td>Alarm</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.emg</td><td>Emergency stop</td><td>string</td><td>-</td><td>-</td></tr><tr><td>alarms</td><td>Current alarms</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>halarms</td><td>Historical alarms</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>oplog</td><td>Operation logs</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>absolute</td><td>Absolute coordinates</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>machine</td><td>Machine coordinates</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>relative</td><td>Relative coordinates</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>distance</td><td>Remaining distance</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>time</td><td>Time</td><td>int32</td><td>m</td><td>power: Power-on time (seconds) cutting: Cutting time (seconds) cycle: Cycle time (seconds) work: Processing time (seconds)</td></tr><tr><td>partCount</td><td>Part count</td><td>int32</td><td>m</td><td>total: Total part count cur: Current part count req: Required part count</td></tr><tr><td>ovfeed</td><td>Current feed override</td><td>double</td><td>-</td><td>-</td></tr><tr><td>ovspindle</td><td>Current spindle override</td><td>double</td><td>-</td><td>-</td></tr><tr><td>actfeed</td><td>Actual feed speed</td><td>double</td><td>-</td><td>-</td></tr><tr><td>actspindle</td><td>Actual spindle speed</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>gcode</td><td>G-code</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>otherCode</td><td>Other codes</td><td>int32</td><td>m</td><td>hcode dcode mcode tcode fcode scode</td></tr><tr><td>macro</td><td>Global variables</td><td>double</td><td>m</td><td>Global variable number</td></tr><tr><td>param</td><td>Parameters</td><td>int32</td><td>m</td><td>Parameter number</td></tr><tr><td>toolOffset</td><td>Tool compensation</td><td>double</td><td>m, n</td><td>m: Tool compensation number. n: RADIUS_GEOM, RADIUS_WEAR, LENGTH_GEOM, LENGTH_WEAR, WEAR_X, WEAR_Z, WEAR_A, LENGTH_X, LENGTH_Y, LENGTH_A, TOOL_NOSE_RADIUS, TOOL_NOSE_R_WEAR, TOOL_NOSE</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>macro and param are readable/writable; others are read-only.</p></div><p><em>CNC Address Examples</em></p><table tabindex="0"><thead><tr><th>Address</th><th>Description</th></tr></thead><tbody><tr><td>systemInfo.nc_ver</td><td>Read system software version</td></tr><tr><td>machine.X</td><td>Read X-axis coordinate</td></tr><tr><td>macro.100</td><td>Read/write global variable 100</td></tr><tr><td>feedOverride</td><td>Read current feed override</td></tr><tr><td>alarms</td><td>Current alarms</td></tr><tr><td>partCount.total</td><td>Total part count</td></tr><tr><td>toolOffset1.RADIUS_GEOM</td><td>Tool compensation 1, radius compensation</td></tr></tbody></table><h3 id="plc-data" tabindex="-1">PLC Data <a class="header-anchor" href="#plc-data" aria-label="Permalink to “PLC Data”">​</a></h3><h3 id="address-format" tabindex="-1">Address Format <a class="header-anchor" href="#address-format" aria-label="Permalink to “Address Format”">​</a></h3><blockquote><p>AREA ADDRESS[.BIT][.LEN]</p></blockquote><table tabindex="0"><thead><tr><th>Identifier</th><th>Description</th><th>Type</th><th>Permission</th></tr></thead><tbody><tr><td>I</td><td>Input Bits</td><td>bit/int8/uint8</td><td>Read</td></tr><tr><td>O</td><td>Output Bits</td><td>bit/int8/uint8</td><td>Read</td></tr><tr><td>C</td><td>C Bits</td><td>bit/int8/uint8</td><td>Read</td></tr><tr><td>S</td><td>S Bits</td><td>bit/int8/uint8</td><td>Read</td></tr><tr><td>A</td><td>A Bits</td><td>bit/int8/uint8</td><td>Read</td></tr><tr><td>R</td><td>Registers</td><td>bit/int32/uint32/int64/uint64/float/double/string</td><td>Read/Write</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>Currently, only partial R area writing is supported; bit writing is not supported.</p></div><blockquote><p>AREA ADDRESS[.setting/value/state]</p></blockquote><table tabindex="0"><thead><tr><th>Identifier</th><th>Description</th><th>Type</th><th>Permission</th></tr></thead><tbody><tr><td>T</td><td>Timer</td><td>int32/uint32</td><td>Read</td></tr><tr><td>N</td><td>Counter</td><td>int32/uint32</td><td>Read</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>When reading timers and counters, specify whether to read the setting value, current value, or state.</p></div><p><em>Common PLC Addresses</em></p><table tabindex="0"><thead><tr><th>Address</th><th>Type</th><th>Description</th></tr></thead><tbody><tr><td>I0.0</td><td>bit</td><td>Input Bits area, address 0 data</td></tr><tr><td>A10</td><td>int8</td><td>A Bits area, address 10 data</td></tr><tr><td>R100</td><td>float</td><td>Register area, address 100 data</td></tr><tr><td>T0.setting</td><td>int32</td><td>Timer area, address 0 setting value</td></tr><tr><td>T0.value</td><td>int32</td><td>Timer area, address 0 current value</td></tr></tbody></table>',22)])])}const h=d(i,[["render",s]]);export{p as __pageData,h as default};
