import{_ as e,c as i,o as a,ah as n,eN as r,eO as o,eP as s,eQ as d}from"./chunks/framework.D2dsaVOY.js";const y=JSON.parse('{"title":"Connect to Industrial Gateway OPC Server","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/configuration/south-devices/opc-ua/igs.md","filePath":"en_US/configuration/south-devices/opc-ua/igs.md"}'),l={name:"en_US/configuration/south-devices/opc-ua/igs.md"};function c(g,t,h,u,p,m){return a(),i("div",null,[...t[0]||(t[0]=[n('<h1 id="connect-to-industrial-gateway-opc-server" tabindex="-1">Connect to Industrial Gateway OPC Server <a class="header-anchor" href="#connect-to-industrial-gateway-opc-server" aria-label="Permalink to “Connect to Industrial Gateway OPC Server”">​</a></h1><h2 id="anonymous-login" tabindex="-1">Anonymous Login <a class="header-anchor" href="#anonymous-login" aria-label="Permalink to “Anonymous Login”">​</a></h2><ol><li><p>Double click on the Industrial Gateway OPC Server icon in the system tray, open the <strong>Project</strong> -&gt; <strong>Property Editor</strong> -&gt; <strong>OPC UA</strong>, and set <strong>Allow anonymous login</strong> to <strong>Yes</strong>. <img src="'+r+'" alt="igs-1_en"></p></li><li><p>Right-click the Industrial Gateway OPC Server icon in the system tray, select <strong>OPC UA Configuration</strong> -&gt; <strong>Server Endpoint</strong>, double-click the endpoint entry, and check all security policies. <img src="'+o+'" alt="igs-2_en"></p></li><li><p>Right-click on the Industrial Gateway OPC Server icon in the system tray and select <strong>Reinitialize</strong> in the menu.</p></li><li><p>GridBeat adds a new southbound OPC UA device, opens <strong>Device Configuration</strong>, fills in the <strong>Endpoint URL</strong> of the target Server, without filling in username/password, without adding certificate/key, and starts the device connection.</p></li></ol><h2 id="certificate-key-anonymous-login" tabindex="-1">Certificate/Key + Anonymous Login <a class="header-anchor" href="#certificate-key-anonymous-login" aria-label="Permalink to “Certificate/Key + Anonymous Login”">​</a></h2><ol><li><p>Set up an anonymous login as described above.</p></li><li><p>Refer to the <a href="./policy.html">Connection policy</a> to generate or convert a certificate/key.</p></li><li><p>Right-click the Industrial Gateway OPC Server icon in the system tray, select <strong>OPC UA Configuration</strong> -&gt; <strong>Trusted Clients</strong>, and import the client certificate in DER format into the list.</p></li></ol><p><img src="'+s+'" alt=""></p><ol start="4"><li>Right-click on the Industrial Gateway OPC Server icon in the system tray and select <strong>Reinitialize</strong> in the menu.</li></ol><h2 id="configure-gridbeat" tabindex="-1">Configure GridBeat <a class="header-anchor" href="#configure-gridbeat" aria-label="Permalink to “Configure GridBeat”">​</a></h2><ol><li><p>Get the PLC measurement point information via the UaExpert software, refer to <a href="./uaexpert.html">UaExpert</a>. <img src="'+d+'" alt="igs-4_en"></p></li><li><p>GridBeat adds a new southbound OPC UA device, opens <strong>Device Configuration</strong>, fills in the <strong>Endpoint URL</strong> of the target Server, does not need to fill in the username/password, adds the certificate/key.</p></li><li><p>Change the <strong>Update Mode</strong> to Subscribe or Read&amp;Subscribe to obtain data via OPC UA subscription.</p></li><li><p>Starts the device connection.</p></li><li><p>Add <strong>Groups</strong> and <strong>Tags</strong> based on the measurement point information.</p></li></ol><h2 id="test-data-list" tabindex="-1">Test Data List <a class="header-anchor" href="#test-data-list" aria-label="Permalink to “Test Data List”">​</a></h2><table tabindex="0"><thead><tr><th>Name</th><th>Address</th><th>Attribute</th><th>Data type</th></tr></thead><tbody><tr><td>BuildDate</td><td>0!2266</td><td>Read</td><td>UINT32</td></tr><tr><td>BuildNumber</td><td>0!2265</td><td>Read</td><td>STRING</td></tr><tr><td>ManufacturerName</td><td>0!2263</td><td>Read</td><td>STRING</td></tr><tr><td>ProductName</td><td>0!2261</td><td>Read</td><td>STRING</td></tr><tr><td>ProductUri</td><td>0!2262</td><td>Read</td><td>STRING</td></tr><tr><td>SoftwareVersion</td><td>0!2264</td><td>Read</td><td>STRING</td></tr></tbody></table>',11)])])}const _=e(l,[["render",c]]);export{y as __pageData,_ as default};
