import{_ as r,c as d,o as s,ah as o,jV as n,jW as a,jX as e,jY as i,jZ as g,j_ as l,j$ as p}from"./chunks/framework.DGOuNAW6.js";const A=JSON.parse('{"title":"Q03UDE 连接示例","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/mitsubishi-3e/q03ude.md","filePath":"zh_CN/configuration/south-devices/mitsubishi-3e/q03ude.md"}'),h={name:"zh_CN/configuration/south-devices/mitsubishi-3e/q03ude.md"};function c(_,t,u,D,m,P){return s(),d("div",null,[...t[0]||(t[0]=[o('<h1 id="q03ude-连接示例" tabindex="-1">Q03UDE 连接示例 <a class="header-anchor" href="#q03ude-连接示例" aria-label="Permalink to “Q03UDE 连接示例”">​</a></h1><p>Q03UDE 是三菱电机 Q 系列 PLC 的一款产品。这款 PLC 适用于大型和复杂的自动化应用，提供了高性能和丰富的功能。</p><p>本节介绍如何通过 GridBeat Mitsubishi 3E 插件连接 Q03UDE 。</p><h2 id="plc-设置" tabindex="-1">PLC 设置 <a class="header-anchor" href="#plc-设置" aria-label="Permalink to “PLC 设置”">​</a></h2><ol><li><p>打开 GX Works2 PLC 编程软件，新建工程，<strong>系列</strong> 选择 <strong>QCPU(Q模式)</strong>，<strong>机型</strong> 选择 <strong>Q03UDE</strong>，点击 <strong>确定</strong>。 <img src="'+n+'" alt="q03ude1"></p></li><li><p>点击 <strong>连接目标</strong> -&gt; <strong>Connection1</strong> -&gt; <strong>可编程控制器直接连接设置</strong> -&gt; <strong>以太网</strong>，分别设置 <strong>适配器</strong> 和 <strong>IP地址</strong>，点击 <strong>确定</strong>。 <img src="'+a+'" alt="q03ude2"></p></li><li><p>点击菜单 <strong>在线</strong> -&gt; <strong>PLC读取</strong> -&gt; <strong>全选</strong> -&gt; <strong>执行</strong>。 <img src="'+e+'" alt="q03ude3"></p></li><li><p>点击 <strong>导航</strong> -&gt; <strong>参数</strong> -&gt; <strong>PLC参数</strong> -&gt; <strong>打开设置</strong>，<strong>协议</strong> 选择 <strong>TCP</strong>，<strong>打开方式</strong> 选择 <strong>MC协议</strong>，设置好需要的 <strong>本站端口号</strong>，点击 <strong>设置结束</strong>。 <img src="'+i+'" alt="q03ude4"></p></li><li><p>根据需要勾选 <strong>允许 RUN 中写入（FTP 与 MC 协议）</strong>。</p></li><li><p>点击菜单 <strong>在线</strong> -&gt; <strong>PLC写入</strong> -&gt; <strong>执行</strong>。</p></li></ol><h2 id="配置-gridbeat" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li><p>在 GridBeat 南向设备管理中添加一个 Mitsubishi 3E 设备。</p></li><li><p>在设备配置中修改 <strong>PLC IP 地址</strong> 为目标设备 IP 地址。</p></li><li><p>在设备配置中修改 <strong>PLC 端口</strong> 为目标设备端口，提交设置表单。 <img src="'+g+'" alt="q03ude5"></p></li><li><p>添加 <strong>组</strong>，添加测试 <strong>点位</strong>。</p></li></ol><h2 id="使用-udp-模式连接-q03ude" tabindex="-1">使用 UDP 模式连接 Q03UDE <a class="header-anchor" href="#使用-udp-模式连接-q03ude" aria-label="Permalink to “使用 UDP 模式连接 Q03UDE”">​</a></h2><ol><li><p>设置 plc 中的 udp 协议，<strong>协议</strong>选择 udp，<strong>端口号</strong>填写 plc 监听端口。 <img src="'+l+'" alt="fx5u7"></p></li><li><p>配置 GridBeat UDP 传输模式，<strong>传输模式</strong>选择 UDP，UDP 源端口使用默认端口号 52001。 <img src="'+p+'" alt="fx5u8"></p></li></ol><h2 id="测试点位" tabindex="-1">测试点位 <a class="header-anchor" href="#测试点位" aria-label="Permalink to “测试点位”">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>地址</th><th>属性</th><th>类型</th></tr></thead><tbody><tr><td>DATA1</td><td>D0</td><td>Read Write</td><td>INT16</td></tr><tr><td>DATA2</td><td>D1</td><td>Read Write</td><td>UINT16</td></tr><tr><td>DATA3</td><td>D2</td><td>Read Write</td><td>INT32</td></tr><tr><td>DATA4</td><td>D4</td><td>Read Write</td><td>UINT32</td></tr><tr><td>DATA5</td><td>D6</td><td>Read Write</td><td>FLOAT</td></tr><tr><td>DATA6</td><td>D8</td><td>Read Write</td><td>DOUBLE</td></tr><tr><td>DATA7</td><td>X0</td><td>Read</td><td>BIT</td></tr><tr><td>DATA8</td><td>Y0</td><td>Read Write</td><td>BIT</td></tr><tr><td>DATA9</td><td>D20.0</td><td>Read</td><td>BIT</td></tr><tr><td>DATA10</td><td>D100.16</td><td>Read Write</td><td>STRING</td></tr></tbody></table>',11)])])}const b=r(h,[["render",c]]);export{A as __pageData,b as default};
