import{_ as i,c as e,o as l,ah as o}from"./chunks/framework.D2dsaVOY.js";const b=JSON.parse('{"title":"Inovance Modbus TCP 使用常见问题","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/modbus-hc-tcp/faq/faq.md","filePath":"zh_CN/configuration/south-devices/modbus-hc-tcp/faq/faq.md"}'),r={name:"zh_CN/configuration/south-devices/modbus-hc-tcp/faq/faq.md"};function t(s,a,d,n,c,h){return l(),e("div",null,[...a[0]||(a[0]=[o('<h1 id="inovance-modbus-tcp-使用常见问题" tabindex="-1">Inovance Modbus TCP 使用常见问题 <a class="header-anchor" href="#inovance-modbus-tcp-使用常见问题" aria-label="Permalink to “Inovance Modbus TCP 使用常见问题”">​</a></h1><h2 id="未读取到点位值-错误码-3002" tabindex="-1">未读取到点位值，错误码 3002 <a class="header-anchor" href="#未读取到点位值-错误码-3002" aria-label="Permalink to “未读取到点位值，错误码 3002”">​</a></h2><hr><ul><li>开启 DEBUG 日志，查看日志，检查数据流，设备是否有回复数据，如果有回复数据，回复的数据是否正确）。</li><li>检查地址是否正确。</li><li>某些设备可能不支持一次只读取一个点位，需要把所有点位都配上（GridBeat 会自动优化批量读取）。</li><li>连接是否通，可使用 telnet 指令检测链路是否有异常。</li><li>设备支持的协议为 Modbus RTU，不是 Modbus TCP，使用 Modbus RTU 插件尝试。</li></ul><h2 id="点位值失效-错误码-3008" tabindex="-1">点位值失效，错误码 3008 <a class="header-anchor" href="#点位值失效-错误码-3008" aria-label="Permalink to “点位值失效，错误码 3008”">​</a></h2><hr><ul><li>设备离线，数据无法更新。</li><li>设备在一定时间内无响应数据。</li><li>设备支持的协议为 Modbus RTU，不是 Modbus TCP，使用 Modbus RTU 插件尝试。</li></ul><h2 id="读取到的点位值与预期不符" tabindex="-1">读取到的点位值与预期不符 <a class="header-anchor" href="#读取到的点位值与预期不符" aria-label="Permalink to “读取到的点位值与预期不符”">​</a></h2><hr><ul><li>数据类型选择不正确。</li><li>点位字节序设置不正确。</li><li>存在多个上游主站采集数据，导致数据出现错乱。</li><li>设备主动上传数据，标准 Modbus 协议基于主设备主导的请求-响应模式，而不是从设备主动上传数据的模式。</li><li>设备支持的协议为 Modbus RTU，不是 Modbus TCP，使用 Modbus RTU 插件尝试。</li></ul><h2 id="导入点位失败" tabindex="-1">导入点位失败 <a class="header-anchor" href="#导入点位失败" aria-label="Permalink to “导入点位失败”">​</a></h2><hr><ul><li>数据类型选择不正确。</li><li>点位地址超过规定范围。</li><li>点位地址格式错误，X 和 Y 区域地址采用八进制。</li></ul>',13)])])}const _=i(r,[["render",t]]);export{b as __pageData,_ as default};
