import{_ as i,c as l,o as e,ah as o}from"./chunks/framework.D2dsaVOY.js";const c=JSON.parse('{"title":"Modbus RTU 使用常见问题","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/modbus-rtu/faq/faq.md","filePath":"zh_CN/configuration/south-devices/modbus-rtu/faq/faq.md"}'),r={name:"zh_CN/configuration/south-devices/modbus-rtu/faq/faq.md"};function s(t,a,d,u,n,h){return e(),l("div",null,[...a[0]||(a[0]=[o('<h1 id="modbus-rtu-使用常见问题" tabindex="-1">Modbus RTU 使用常见问题 <a class="header-anchor" href="#modbus-rtu-使用常见问题" aria-label="Permalink to “Modbus RTU 使用常见问题”">​</a></h1><h2 id="未读取到点位值-错误码-3002" tabindex="-1">未读取到点位值，错误码 3002 <a class="header-anchor" href="#未读取到点位值-错误码-3002" aria-label="Permalink to “未读取到点位值，错误码 3002”">​</a></h2><hr><ul><li>开启 DEBUG 日志，查看日志，检查数据流，设备是否有回复数据，如果有回复数据，回复的数据是否正确（串口设备如果有多个上游主站采集时，会产生异常）。</li><li>检查站点号是否正确。</li><li>检查地址是否正确（GridBeat 的 Modbus 地址从 1 开始）。</li><li>某些设备可能不支持一次只读取一个点位，需要把所有点位都配上（GridBeat 会自动优化批量读取）。</li><li>串口接线异常。</li><li>串口参数配置错误。</li><li>设备支持的协议为 Modbus TCP，不是 Modbus RTU，使用 Modbus TCP 插件尝试。</li></ul><h2 id="点位值失效-错误码-3008" tabindex="-1">点位值失效，错误码 3008 <a class="header-anchor" href="#点位值失效-错误码-3008" aria-label="Permalink to “点位值失效，错误码 3008”">​</a></h2><hr><ul><li>设备离线，数据无法更新。</li><li>设备在一定时间内无响应数据。</li><li>设备支持的协议为 Modbus TCP，不是 Modbus RTU，使用 Modbus TCP 插件尝试。</li></ul><h2 id="读取到的点位值与预期不符" tabindex="-1">读取到的点位值与预期不符 <a class="header-anchor" href="#读取到的点位值与预期不符" aria-label="Permalink to “读取到的点位值与预期不符”">​</a></h2><hr><ul><li>数据类型选择不正确。</li><li>点位字节序设置不正确。</li><li>存在多个上游主站采集数据，导致数据出现错乱。</li><li>DTU 开启了注册包或心跳包，导致 GridBeat 认为数据流数据错误。</li><li>设备主动上传数据，标准 Modbus 协议基于主设备主导的请求-响应模式，而不是从设备主动上传数据的模式。</li><li>设备支持的协议为 Modbus TCP，不是 Modbus RTU，使用 Modbus TCP 插件尝试。</li></ul>',10)])])}const _=i(r,[["render",s]]);export{c as __pageData,_ as default};
