import{_ as d,c as r,o as i,ah as e}from"./chunks/framework.D2dsaVOY.js";const c=JSON.parse('{"title":"MITSUBISHI CNC","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/neuhub/mitsubishi-cnc.md","filePath":"zh_CN/configuration/south-devices/neuhub/mitsubishi-cnc.md"}'),a={name:"zh_CN/configuration/south-devices/neuhub/mitsubishi-cnc.md"};function b(n,t,l,o,s,m){return i(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="mitsubishi-cnc" tabindex="-1">MITSUBISHI CNC <a class="header-anchor" href="#mitsubishi-cnc" aria-label="Permalink to “MITSUBISHI CNC”">​</a></h1><p>GridBeat 可以使用 GridBeat HUB 驱动和 GridBeatHUB Windows 程序，间接访问三菱 M70,M80 CNC 系统。以实时采集多种设备运行数据，包括程序名，进给速度，运行状态，功耗, PLC 点位, 全局变量，参数等。</p><h2 id="neuron-hub-windows-程序参数" tabindex="-1">NEURON HUB Windows 程序参数 <a class="header-anchor" href="#neuron-hub-windows-程序参数" aria-label="Permalink to “NEURON HUB Windows 程序参数”">​</a></h2><table tabindex="0"><thead><tr><th>参数</th><th>说明</th></tr></thead><tbody><tr><td>Node Name</td><td>节点名称，必须唯一，用来区分多个节点</td></tr><tr><td>Host</td><td>需要连接 CNC 设备 IP 地址</td></tr><tr><td>cnctype</td><td>设备类型，目前支持 M700L,M700M,M800L,M800M</td></tr><tr><td>cardno</td><td>控制卡号，默认填写 1</td></tr></tbody></table><h2 id="支持的数据类型" tabindex="-1">支持的数据类型 <a class="header-anchor" href="#支持的数据类型" aria-label="Permalink to “支持的数据类型”">​</a></h2><ul><li>uint8</li><li>int8</li><li>uint32</li><li>int32</li><li>uint64</li><li>int64</li><li>float</li><li>double</li><li>bit</li><li>string</li><li>ARRAY_STRING</li><li>ARRAY_DOUBLE</li></ul><h2 id="cnc-数据" tabindex="-1">CNC 数据 <a class="header-anchor" href="#cnc-数据" aria-label="Permalink to “CNC 数据”">​</a></h2><blockquote><p>address[.m][.n][.k][.j]</p></blockquote><table tabindex="0"><thead><tr><th>tag 标识（地址）</th><th>说明</th><th>数据类型</th><th>参数</th><th>备注</th></tr></thead><tbody><tr><td>systemStatus</td><td>当前运行状态</td><td>int32</td><td>m</td><td>m：0 对刀状态 1 自动状态 2 自动运行状态 3 自动暂停状态</td></tr><tr><td>spindleInfo</td><td>主轴状态</td><td>int64</td><td>m n</td><td>m：轴编号 n：0 增益 1： 位置偏差 2：电机转速 3：负载 5：报警1 6：报警2 7：报警3 8：报警4</td></tr><tr><td>servoInfo</td><td>伺服轴状态</td><td>int64</td><td>m n</td><td>m：轴编号 n：0 增益 1： 位置偏差 2：电机转速 3：电流 6：负载</td></tr><tr><td>work</td><td>工件坐标</td><td>double</td><td>m</td><td>m：轴编号</td></tr><tr><td>machine</td><td>机械坐标</td><td>double</td><td>m</td><td>m：轴编号</td></tr><tr><td>relative</td><td>相对坐标</td><td>double</td><td>m</td><td>m：轴编号</td></tr><tr><td>distance</td><td>剩余距离</td><td>double</td><td>m</td><td>m：轴编号</td></tr><tr><td>feedSpeed</td><td>进给速度</td><td>double</td><td>m</td><td>0 FA 1 FM 2 FS 3 Fc 4 FE</td></tr><tr><td>param</td><td>参数</td><td>ARRAY_STRING</td><td>m n k j</td><td>m: 轴编号 n：组号 k： 参数号 j：参数个数</td></tr><tr><td>toolOffset</td><td>刀补</td><td>double</td><td>m n k</td><td>m：类型 n：刀补类型 k：编号</td></tr><tr><td>alarm</td><td>报警</td><td>ARRAY_STRING</td><td>-</td><td>-</td></tr><tr><td>runTime</td><td>自动运行时间</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>startTime</td><td>自动开始时间</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>aliveTime</td><td>上电时间</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>estimateTime</td><td>外部集成时间</td><td>int32</td><td>m</td><td>1：定时器1 2：定时器2</td></tr><tr><td>commonVar</td><td>全局变量</td><td>double</td><td>m</td><td>变量编号</td></tr><tr><td>localVar</td><td>局部变量</td><td>double</td><td>m n</td><td>m:变量编号 n: 等级</td></tr><tr><td>invalidStatus</td><td>无效状态</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>commandStatus</td><td>操作命令状态</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>cuttingMode</td><td>切削模式</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>mainProgram</td><td>主程序</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>subProgram</td><td>子程序</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>mainSeqNum</td><td>主序列号</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>subSeqNum</td><td>子序列号</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>programCurrentBlock</td><td>当前程序断</td><td>string</td><td>-</td><td>-</td></tr><tr><td>powerConsumption</td><td>功耗</td><td>ARRAY_DOUBLE</td><td>m</td><td>m：轴编号 [0]：系统总功耗 [1]：伺服功耗 [3]：主轴功耗</td></tr><tr><td>toolLife</td><td>刀具寿命</td><td>ARRAY_STRING</td><td>m n</td><td>m：组编号 n：刀具编号</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>commonVar 可读写，其他只读。</p></div><p><em>CNC 地址示例</em></p><table tabindex="0"><thead><tr><th>地址</th><th>说明</th></tr></thead><tbody><tr><td>systemStatus.0</td><td>对刀状态</td></tr><tr><td>machine.1</td><td>读取轴1坐标</td></tr><tr><td>commonVar.100</td><td>读写 100 号全局变量</td></tr><tr><td>feedSpeed.0</td><td>读取当前进给速度</td></tr><tr><td>alarm</td><td>当前报警列表</td></tr><tr><td>param.1.30.8002.1</td><td>加工件数</td></tr></tbody></table><h3 id="plc-数据" tabindex="-1">PLC 数据 <a class="header-anchor" href="#plc-数据" aria-label="Permalink to “PLC 数据”">​</a></h3><table tabindex="0"><thead><tr><th>标识</th><th>说明</th><th>类型</th><th>权限</th></tr></thead><tbody><tr><td>B</td><td>计数器（固定计数器）</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>C</td><td>计数器线圈</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>D</td><td>数据寄存器</td><td>16bit/32bit</td><td>读写</td></tr><tr><td>E</td><td>特殊继电器</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>F</td><td>报警消息临时内存</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>G</td><td>临时存储器</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>I</td><td>设备</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>J</td><td>J设备</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>L</td><td>锁存继电器（备份存储器）</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>M</td><td>临时存储器</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>Q</td><td>Q设备</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>R</td><td>文件寄存器</td><td>16bit/32bit</td><td>读写</td></tr><tr><td>SM</td><td>特殊继电器（用于链接）</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>SD</td><td>特殊寄存器</td><td>16bit/32bit</td><td>读写</td></tr><tr><td>ST</td><td>累积定时器</td><td>16bit/32bit</td><td>读写</td></tr><tr><td>SW</td><td>特殊寄存器（用于链接）</td><td>16bit/32bit</td><td>读写</td></tr><tr><td>T</td><td>10ms定时器单元</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>U</td><td>输入信号线至可编程控制器</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>V</td><td>V 设备</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>W</td><td>输入信号至可编程控制器</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>X</td><td>输入信号</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>Y</td><td>输出信号</td><td>bit/16bit/32bit</td><td>读写</td></tr><tr><td>ZR</td><td>文件寄存器</td><td>16bit/32bit</td><td>读写</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>地址输入形式为 16 进制。</p></div><p><em>PLC 常用点位</em></p><table tabindex="0"><thead><tr><th>地址</th><th>类型</th><th>说明</th></tr></thead><tbody><tr><td>R69</td><td>uint16</td><td>PLC R 区域，地址69的数据，其值为EMG急停标志（65519 ON 65535 OFF）</td></tr><tr><td>R2500</td><td>uint16</td><td>PLC R 区域，地址2500的数据，其值为进给倍率</td></tr><tr><td>R7008</td><td>uint16</td><td>PLC R 区域，地址7008的数据，其值为主轴倍率</td></tr><tr><td>R6506</td><td>uint32</td><td>PLC R 区域，地址6506的数据，其值为主轴实际旋转转速</td></tr><tr><td>R7000</td><td>uint32</td><td>PLC R 区域，地址7000~7001的数据，其值为指令设定主轴转速</td></tr><tr><td>R6525</td><td>uint16</td><td>PLC R 区域，地址6525的数据，其值为主轴负载</td></tr><tr><td>R6529</td><td>uint16</td><td>PLC R 区域，地址6529的数据，其值为主轴报警编号</td></tr><tr><td>R606</td><td>uint32</td><td>PLC R 区域，地址606 ~ 607 的数据，其值为工件加工数当前值</td></tr><tr><td>R608</td><td>uint32</td><td>PLC R 区域，地址608 ~ 609 的数据，其值为工件加工数最大值</td></tr><tr><td>R11824</td><td>uint32</td><td>PLC R 区域，地址11824 ~ 11825 的数据，其值为使用中刀具组号</td></tr><tr><td>R11826</td><td>uint32</td><td>PLC R 区域，地址11826 ~ 11827 的数据，其值为使用中刀具刀号号</td></tr><tr><td>R11830</td><td>uint32</td><td>PLC R 区域，地址11830 ~ 11831 的数据，其值为使用中刀具使用累计时间数据</td></tr><tr><td>R11832</td><td>uint32</td><td>PLC R 区域，地址11832 ~ 11833 的数据，其值为 使用中刀具寿命设定时间数据</td></tr><tr><td>XC00</td><td>uint16</td><td>PLC X 区域，地址 C00 ~ C0F 的数据，其值为控制模式（1 JOG 模式中 2 手轮模式中 4 增量模式中 8 手动任意进给模式中 16 参考点返回模式中 32 自动初始设定模式中 256 内存模式中 2048 MDI 模式中）</td></tr><tr><td>XC12</td><td>bit</td><td>PLC X 区域，地址 C12 的数据，其值为自动运行中</td></tr><tr><td>XC13</td><td>bit</td><td>PLC X 区域，地址 C13 的数据，其值为自动运行启动中</td></tr><tr><td>XC14</td><td>bit</td><td>PLC X 区域，地址 C14 的数据，其值为自动运行暂停中</td></tr><tr><td>XC15</td><td>bit</td><td>PLC X 区域，地址 C15 的数据，其值为复位中</td></tr><tr><td>XC20</td><td>bit</td><td>PLC X 区域，地址 C20 的数据，其值为快速进给中</td></tr><tr><td>XC21</td><td>bit</td><td>PLC X 区域，地址 C21 的数据，其值为切削进给中</td></tr><tr><td>XC22</td><td>bit</td><td>PLC X 区域，地址 C22 的数据，其值为攻丝中</td></tr><tr><td>XC23</td><td>bit</td><td>PLC X 区域，地址 C23 的数据，其值为螺纹切削中</td></tr><tr><td>XC24</td><td>bit</td><td>PLC X 区域，地址 C24 的数据，其值为同步进给中</td></tr><tr><td>XC25</td><td>bit</td><td>PLC X 区域，地址 C25 的数据，其值为恒速中</td></tr><tr><td>XC26</td><td>bit</td><td>PLC X 区域，地址 C26 的数据，其值为跳跃中</td></tr><tr><td>XC27</td><td>bit</td><td>PLC X 区域，地址 C27 的数据，其值为参考点返回中</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>PLC 数据表可以查看三菱官网提供的《PLC开发说明书-M800/M80/E80系列》，获取更多系统运行数据。</p></div>',18)])])}const C=d(a,[["render",b]]);export{c as __pageData,C as default};
