import{_ as d,c as r,o as e,ah as a}from"./chunks/framework.DGOuNAW6.js";const u=JSON.parse('{"title":"SYNTEC CNC","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/neuhub/syntec-cnc.md","filePath":"zh_CN/configuration/south-devices/neuhub/syntec-cnc.md"}'),i={name:"zh_CN/configuration/south-devices/neuhub/syntec-cnc.md"};function n(s,t,o,l,c,h){return e(),r("div",null,[...t[0]||(t[0]=[a('<h1 id="syntec-cnc" tabindex="-1">SYNTEC CNC <a class="header-anchor" href="#syntec-cnc" aria-label="Permalink to “SYNTEC CNC”">​</a></h1><p>GridBeat 可以使用 GridBeat HUB 驱动和 GridBeatHUB Windows 程序，间接访问新代 CNC 系统。以实时采集多种设备运行数据，包括程序名，主轴倍率，运行状态，PLC 点位, 参数，全局变量等。</p><h2 id="neuron-hub-windows-程序参数" tabindex="-1">NEURON HUB Windows 程序参数 <a class="header-anchor" href="#neuron-hub-windows-程序参数" aria-label="Permalink to “NEURON HUB Windows 程序参数”">​</a></h2><table tabindex="0"><thead><tr><th>参数</th><th>说明</th></tr></thead><tbody><tr><td>Node Name</td><td>节点名称，必须唯一，用来区分多个节点</td></tr><tr><td>Host</td><td>需要连接 CNC 设备 IP 地址</td></tr></tbody></table><h2 id="支持的数据类型" tabindex="-1">支持的数据类型 <a class="header-anchor" href="#支持的数据类型" aria-label="Permalink to “支持的数据类型”">​</a></h2><ul><li>uint8</li><li>int8</li><li>uint32</li><li>int32</li><li>uint64</li><li>int64</li><li>float</li><li>double</li><li>bit</li><li>string</li><li>ARRAY_STRING</li></ul><h2 id="cnc-数据" tabindex="-1">CNC 数据 <a class="header-anchor" href="#cnc-数据" aria-label="Permalink to “CNC 数据”">​</a></h2><blockquote><p>address[.m][.n]</p></blockquote><table tabindex="0"><thead><tr><th>tag 标识（地址）</th><th>说明</th><th>数据类型</th><th>参数</th><th>备注</th></tr></thead><tbody><tr><td>systemInfo.axes</td><td>可控制轴数</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>systemInfo.cnc_type</td><td>系统类型</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.max_axes</td><td>最大轴数</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>systemInfo.series</td><td>M/T 类型</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.nc_ver</td><td>系统软件版本</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemInfo.axis_name</td><td>各轴坐标名称</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.status</td><td>当前运行状态</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.main_prog</td><td>主程序名</td><td>string</td><td>-</td><td>0: 录入方式 1: 自动方式 2: 无效方式 3: 编辑方式 4: 单步方式 5: 手动方式 8: 手轮方式 9: 机械回零方式 10: 程序回零方式</td></tr><tr><td>systemStatus.cur_prog</td><td>目前执行程序名</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.mode</td><td>模式</td><td>string</td><td>-</td><td>0x1: 急停信号有效 0x2: 伺服准备未绪 0x4: IO 准备未绪（远程 IO 设备等）</td></tr><tr><td>systemStatus.alarm</td><td>报警</td><td>string</td><td>-</td><td>-</td></tr><tr><td>systemStatus.emg</td><td>紧停</td><td>string</td><td>-</td><td>-</td></tr><tr><td>alarms</td><td>当前报警</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>halarms</td><td>历史报警</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>oplog</td><td>操作记录</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>absolute</td><td>绝对坐标</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>machine</td><td>机械坐标</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>relative</td><td>相对坐标</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>distance</td><td>剩余距离</td><td>double</td><td>m</td><td>X Y Z</td></tr><tr><td>time</td><td>时间</td><td>int32</td><td>m</td><td>power: 上电时间（单位：秒） cutting: 切削时间（单位：秒）cycle: 循环时间（单位：秒） work: 加工时间（单位：秒）</td></tr><tr><td>partCount</td><td>加工计数</td><td>int32</td><td>m</td><td>total: 总加工计数 cur: 当前加工计数 req: 要求加工件数</td></tr><tr><td>ovfeed</td><td>当前进给倍率</td><td>double</td><td>-</td><td>-</td></tr><tr><td>ovspindle</td><td>当前主轴倍率</td><td>double</td><td>-</td><td>-</td></tr><tr><td>actfeed</td><td>实际进给速度</td><td>double</td><td>-</td><td>-</td></tr><tr><td>actspindle</td><td>实际主轴转速</td><td>int32</td><td>-</td><td>-</td></tr><tr><td>gcode</td><td>G 代码</td><td>array string</td><td>-</td><td>-</td></tr><tr><td>otherCode</td><td>Other 代码</td><td>int32</td><td>m</td><td>hcode dcode mcode tcode fcode scode</td></tr><tr><td>macro</td><td>全局变量</td><td>double</td><td>m</td><td>全局变量编号</td></tr><tr><td>param</td><td>参数</td><td>int32</td><td>m</td><td>参数编号</td></tr><tr><td>toolOffset</td><td>刀补</td><td>double</td><td>m，n</td><td>m：刀补编号。 n：RADIUS_GEOM，RADIUS_WEAR，LENGTH_GEOM，LENGTH_WEAR，WEAR_X，WEAR_Z，WEAR_A，LENGTH_X，LENGTH_Y，LENGTH_A， TOOL_NOSE_RADIUS，TOOL_NOSE_R_WEAR，TOOL_NOSE</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>macro，param 可读写，其他只读。</p></div><p><em>CNC 地址示例</em></p><table tabindex="0"><thead><tr><th>地址</th><th>说明</th></tr></thead><tbody><tr><td>systemInfo.nc_ver</td><td>读取系统软件版本</td></tr><tr><td>machine.X</td><td>读取 X 轴坐标</td></tr><tr><td>macro.100</td><td>读写 100 号全局变量</td></tr><tr><td>feedOverride</td><td>读取当前进给倍率</td></tr><tr><td>alarms</td><td>当前报警</td></tr><tr><td>partCount.total</td><td>总加工计数</td></tr><tr><td>toolOffset1.RADIUS_GEOM</td><td>刀补1，半径补偿</td></tr></tbody></table><h3 id="plc-数据" tabindex="-1">PLC 数据 <a class="header-anchor" href="#plc-数据" aria-label="Permalink to “PLC 数据”">​</a></h3><h3 id="地址格式" tabindex="-1">地址格式 <a class="header-anchor" href="#地址格式" aria-label="Permalink to “地址格式”">​</a></h3><blockquote><p>AREA ADDRESS[.BIT][.LEN]</p></blockquote><table tabindex="0"><thead><tr><th>标识</th><th>说明</th><th>类型</th><th>权限</th></tr></thead><tbody><tr><td>I</td><td>Input Bits</td><td>bit/int8/uint8</td><td>读</td></tr><tr><td>O</td><td>Output Bits</td><td>bit/int8/uint8</td><td>读</td></tr><tr><td>C</td><td>C Bits</td><td>bit/int8/uint8</td><td>读</td></tr><tr><td>S</td><td>S Bits</td><td>bit/int8/uint8</td><td>读</td></tr><tr><td>A</td><td>A Bits</td><td>bit/int8/uint8</td><td>读</td></tr><tr><td>R</td><td>寄存器</td><td>bit/int32/uint32/int64/uint64/float/double/string</td><td>读/写</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>目前只支持设置部分 R 区，不支持 bit 写入。</p></div><blockquote><p>AREA ADDRESS[.setting/value/state]</p></blockquote><table tabindex="0"><thead><tr><th>标识</th><th>说明</th><th>类型</th><th>权限</th></tr></thead><tbody><tr><td>T</td><td>定时器</td><td>int32/uint32</td><td>读</td></tr><tr><td>N</td><td>计数器</td><td>int32/uint32</td><td>读</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>读取定时器和计数器时，需要指定是设置值，当前值还是状态。</p></div><p><em>PLC 常用点位</em></p><table tabindex="0"><thead><tr><th>地址</th><th>类型</th><th>说明</th></tr></thead><tbody><tr><td>I0.0</td><td>bit</td><td>Input Bits 区域，地址0的数据</td></tr><tr><td>A10</td><td>int8</td><td>A Bits 区域，地址10的数据</td></tr><tr><td>R100</td><td>float</td><td>寄存器 区域，地址100的数据</td></tr><tr><td>T0.setting</td><td>int32</td><td>定时器 区域，地址0的设定值数据</td></tr><tr><td>T0.value</td><td>int32</td><td>定时器 区域，地址0的当前数据</td></tr></tbody></table>',22)])])}const m=d(i,[["render",n]]);export{u as __pageData,m as default};
