import{_ as e,c as a,o as d,ah as r}from"./chunks/framework.DGOuNAW6.js";const p=JSON.parse('{"title":"OPC DA","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/opc-da/overview.md","filePath":"zh_CN/configuration/south-devices/opc-da/overview.md"}'),o={name:"zh_CN/configuration/south-devices/opc-da/overview.md"};function i(l,t,h,c,n,s){return d(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="opc-da" tabindex="-1">OPC DA <a class="header-anchor" href="#opc-da" aria-label="Permalink to “OPC DA”">​</a></h1><p>GridBeat 可通过外部辅助程序 neuopc.exe 间接访问运行于 Windows 操作系统的 OPC DA 服务器。NeuOPC 通过将 DA 协议转换为 UA 协议，再通过 GridBeat 已有的 OPC UA 插件进行数据获取，DA 的所有可访问点位都被映射至 UA 的&quot;命名空间2&quot;当中，点位的 ID 则与 DA 保持一致。</p><p>NeuOPC 的组件包可以前往 NeuOPC 的<a href="https://github.com/neugates/neuopc" target="_blank" rel="noreferrer">项目页面</a>下载（NeuOPC 是 GPL 协议下的开源项目）。安装以及远程连接的系统配置参考 <a href="./install.html">安装 NeuOPC</a>和 <a href="./remote.html">NeuOPC 远程访问</a>。</p><h2 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to “参数”">​</a></h2><h3 id="neuopc-参数" tabindex="-1">NeuOPC 参数 <a class="header-anchor" href="#neuopc-参数" aria-label="Permalink to “NeuOPC 参数”">​</a></h3><table tabindex="0"><thead><tr><th>参数</th><th>说明</th></tr></thead><tbody><tr><td>OPC DA Host</td><td>需要连接目标主机标识，可以是目标 IP 或者 Hostname，本机可以不设置</td></tr><tr><td>OPC DA Server</td><td>DA 服务器的名称，如 <code>opcda://192.168.10.133/Matrikon.OPC.Simulation</code>，填写 DA Host 之后可以点击下拉按钮尝试获取 Server 列表</td></tr><tr><td>OPC UA Endpoint</td><td>UA 服务器的监听地址，默认 <code>opc.tcp://localhost:48401</code></td></tr><tr><td>OPC UA User</td><td>UA 服务器的授权访问用户名，默认 <code>admin</code></td></tr><tr><td>OPC UA Password</td><td>UA 服务器的访问密码，默认 <code>123456</code></td></tr></tbody></table><h3 id="gridbeat-opcua-参数" tabindex="-1">GridBeat opcua 参数 <a class="header-anchor" href="#gridbeat-opcua-参数" aria-label="Permalink to “GridBeat opcua 参数”">​</a></h3><table tabindex="0"><thead><tr><th>参数</th><th>说明</th></tr></thead><tbody><tr><td>端点 URL</td><td>NeuOPC 的访问地址，默认是<code>opc.tcp://127.0.0.1:4840/</code></td></tr><tr><td>用户名</td><td>NeuOPC 的授权用户名</td></tr><tr><td>密码</td><td>NeuOPC 的访问密码</td></tr></tbody></table><h2 id="数据类型" tabindex="-1">数据类型 <a class="header-anchor" href="#数据类型" aria-label="Permalink to “数据类型”">​</a></h2><ul><li>INT8（用于表示 SBYTE 类型）</li><li>INT16</li><li>INT32</li><li>INT64</li><li>UINT8（用于表示 BYTE 类型）</li><li>UINT16</li><li>UINT32（同时用于表示 DATETIME 类型）</li><li>UINT64</li><li>FLOAT</li><li>DOUBLE</li><li>BOOL</li><li>STRING</li></ul><h2 id="地址格式" tabindex="-1">地址格式 <a class="header-anchor" href="#地址格式" aria-label="Permalink to “地址格式”">​</a></h2><blockquote><p>IX!NODEID</p></blockquote><p><strong>IX</strong> 名字空间索引，访问 NeuOPC 时，IX 只能为2。</p><p><strong>NODEID</strong> 节点 ID，与 DA 服务器中的字符串一致。</p><h2 id="地址示例" tabindex="-1">地址示例 <a class="header-anchor" href="#地址示例" aria-label="Permalink to “地址示例”">​</a></h2><table tabindex="0"><thead><tr><th>地址</th><th>数据类型</th><th>说明</th></tr></thead><tbody><tr><td>2!Bucket Brigade.UInt2</td><td>UINT16</td><td>获取类型为 UINT16 的数据点；NS 为2，NODEID 为 Bucket Brigade.UInt2</td></tr></tbody></table>',16)])])}const P=e(o,[["render",i]]);export{p as __pageData,P as default};
