import{_ as a,c as e,o as d,ah as i,kZ as o,k_ as s,k$ as r,l0 as n}from"./chunks/framework.DGOuNAW6.js";const S=JSON.parse('{"title":"Industrial Gateway OPC Server 连接示例","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/opc-ua/igs.md","filePath":"zh_CN/configuration/south-devices/opc-ua/igs.md"}'),l={name:"zh_CN/configuration/south-devices/opc-ua/igs.md"};function g(h,t,p,c,u,P){return d(),e("div",null,[...t[0]||(t[0]=[i('<h1 id="industrial-gateway-opc-server-连接示例" tabindex="-1">Industrial Gateway OPC Server 连接示例 <a class="header-anchor" href="#industrial-gateway-opc-server-连接示例" aria-label="Permalink to “Industrial Gateway OPC Server 连接示例”">​</a></h1><p>Industrial Gateway OPC Server 是 Matrikon 提供的 OPC 服务器，可用于通过 OPC 协议来收集和分发工业设备的数据。</p><p>本节将演示如何通过 GridBeat OPC UA 插件连接 Industrial Gateway OPC Server。</p><h2 id="连接-opc-ua-server-匿名登录" tabindex="-1">连接 OPC UA Server（匿名登录） <a class="header-anchor" href="#连接-opc-ua-server-匿名登录" aria-label="Permalink to “连接 OPC UA Server（匿名登录）”">​</a></h2><ol><li>双击系统托盘中的 Industrial Gateway OPC Server 图标，在主界面中打开 <strong>项目</strong> -&gt; <strong>属性编辑器</strong> -&gt; <strong>OPC UA</strong> 将 <strong>允许匿名登录</strong>设置为 <strong>是</strong>。 <img src="'+o+'" alt="igs-1"></li><li>右键点击系统托盘中的 Industrial Gateway OPC Server 图标，在菜单中选择 <strong>OPC UA 配置</strong> -&gt; <strong>服务器端点</strong>，双击端点条目，勾选所有安全策略。 <img src="'+s+'" alt="igs-2"></li><li>右键点击系统托盘中的 Industrial Gateway OPC Server 图标，在菜单中选择 <strong>重新初始化</strong>。</li></ol><h2 id="配置-gridbeat" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li>通过 UaExpert 软件查看 Industrial Gateway OPC Server 测点信息，参考 <a href="./uaexpert.html">UaExpert</a>。 <img src="'+r+'" alt="igs-4"></li><li>GridBeat 新增南向 OPC UA 设备，打开 <strong>设备配置</strong>，填写目标 Server 的 <strong>端点 URL</strong>，无需填写用户名/密码，无需添加证书/密钥，启动设备连接。</li><li>根据测点信息添加 <strong>Groups</strong> 和 <strong>Tags</strong>。</li></ol><h2 id="连接-opc-ua-server-证书-密钥-匿名登录" tabindex="-1">连接 OPC UA Server（证书/密钥 + 匿名登录） <a class="header-anchor" href="#连接-opc-ua-server-证书-密钥-匿名登录" aria-label="Permalink to “连接 OPC UA Server（证书/密钥 + 匿名登录）”">​</a></h2><ol><li><p>按照上文设置匿名登录；</p></li><li><p>参考<a href="./policy.html">连接策略</a>生成或转换证书/密钥；</p></li><li><p>右键点击系统托盘中的 Industrial Gateway OPC Server 图标，在菜单中选择 <strong>OPC UA 配置</strong> -&gt; <strong>受信任的客户端</strong>，将 DER 格式的客户端证书导入列表。 <img src="'+n+'" alt="igs-3"></p></li><li><p>右键点击系统托盘中的 Industrial Gateway OPC Server 图标，在菜单中选择 <strong>重新初始化</strong>。</p></li></ol><h2 id="配置-gridbeat-1" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat-1" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li><p>通过 UaExpert 软件查看 Industrial Gateway OPC Server 测点信息， 参考 <a href="./uaexpert.html">UaExpert</a>。 <img src="'+r+'" alt="igs-4"></p></li><li><p>GridBeat 新增南向 OPC UA 设备，打开 <strong>设备配置</strong>，填写目标 Server 的<strong>端点 URL</strong>，无需填写用户名/密码，添加证书/密钥。</p></li><li><p>可以修改<strong>更新模式</strong>为 Subscribe 或 Read&amp;Subscribe，以 OPC UA 订阅方式获取数据。</p></li><li><p>启动设备连接。</p></li><li><p>根据测点信息添加 <strong>Groups</strong> 和 <strong>Tags</strong>。</p></li></ol><h2 id="测试点位" tabindex="-1">测试点位 <a class="header-anchor" href="#测试点位" aria-label="Permalink to “测试点位”">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>地址</th><th>属性</th><th>类型</th></tr></thead><tbody><tr><td>Boolean1</td><td>2!数据类型示例.16 位设备.R 寄存器.Boolean1</td><td>Read Write</td><td>BOOL</td></tr><tr><td>DWord1</td><td>2!数据类型示例.16 位设备.R 寄存器.DWord1</td><td>Read Write</td><td>UINT32</td></tr><tr><td>Double1</td><td>2!数据类型示例.16 位设备.R 寄存器.Double1</td><td>Read Write</td><td>DOUBLE</td></tr><tr><td>Float1</td><td>2!数据类型示例.16 位设备.R 寄存器.Float1</td><td>Read Write</td><td>FLOAT</td></tr><tr><td>LLong1</td><td>2!数据类型示例.16 位设备.R 寄存器.LLong1</td><td>Read Write</td><td>INT64</td></tr><tr><td>Long1</td><td>2!数据类型示例.16 位设备.R 寄存器.Long1</td><td>Read Write</td><td>INT32</td></tr><tr><td>QWord1</td><td>2!数据类型示例.16 位设备.R 寄存器.QWord1</td><td>Read Write</td><td>UINT64</td></tr><tr><td>Short1</td><td>2!数据类型示例.16 位设备.R 寄存器.Short1</td><td>Read Write</td><td>INT16</td></tr><tr><td>Word1</td><td>2!数据类型示例.16 位设备.R 寄存器.Word1</td><td>Read Write</td><td>UINT16</td></tr><tr><td>String1</td><td>2!数据类型示例.16 位设备.S 寄存器.String1</td><td>Read Write</td><td>STRING</td></tr></tbody></table>',13)])])}const C=a(l,[["render",g]]);export{S as __pageData,C as default};
