import{_ as s,c as o,o as e,ah as a,l6 as i,l7 as n,l8 as r,l9 as l,la as g}from"./chunks/framework.D2dsaVOY.js";const U=JSON.parse('{"title":"Prosys Simulation Server 连接示例","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/configuration/south-devices/opc-ua/prosys.md","filePath":"zh_CN/configuration/south-devices/opc-ua/prosys.md"}'),d={name:"zh_CN/configuration/south-devices/opc-ua/prosys.md"};function p(h,t,c,u,m,S){return e(),o("div",null,[...t[0]||(t[0]=[a('<h1 id="prosys-simulation-server-连接示例" tabindex="-1">Prosys Simulation Server 连接示例 <a class="header-anchor" href="#prosys-simulation-server-连接示例" aria-label="Permalink to “Prosys Simulation Server 连接示例”">​</a></h1><p>Prosys Simulation Server 是由 Prosys 公司开发的 OPC UA 服务器，用于模拟 OPC UA 设备和系统的行为，主要用于开发和测试 OPC UA 客户端应用程序。</p><p>需要先将 Prosys Simulation Server 切换到 <strong>Expert Mode</strong>，点击菜单 <strong>Options</strong> -&gt; <strong>Switch to Expert Mode</strong>。</p><h2 id="连接-opc-ua-server-匿名登录" tabindex="-1">连接 OPC UA Server（匿名登录） <a class="header-anchor" href="#连接-opc-ua-server-匿名登录" aria-label="Permalink to “连接 OPC UA Server（匿名登录）”">​</a></h2><ol><li>Prosys OPC UA Simulation Server 界面中切换到 <strong>Endpoints</strong> -&gt; <strong>Security Modes</strong> 取消选择 <strong>Sign</strong> 和 <strong>Sign&amp;Encrypt</strong>，选择 <strong>None</strong>。 <img src="'+i+'" alt="prosys-1"></li><li>Prosys OPC UA Simulation Server 界面中切换到 <strong>Users</strong> -&gt; <strong>User Authentication Methods</strong> 取消选择 <strong>Username&amp;Password</strong>、<strong>Certificate</strong> 和 <strong>IssuedToken/External System</strong>，选择 <strong>Anonymous</strong>。 <img src="'+n+'" alt="prosys-2"></li><li>保存设置并重新启动 Prosys OPC UA Simulation Server。</li></ol><h2 id="配置-gridbeat" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li><p>通过 UaExpert 软件查看 Ignition 测点信息， 参考 <a href="./uaexpert.html">配置 UaExpert</a>。 <img src="'+r+'" alt="prosys-5"></p></li><li><p>GridBeat 新增南向 OPC UA 设备，打开 <strong>设备配置</strong>并进行如下设置后，启动设备连接。</p><ul><li><p><strong>端点 URL</strong>：填写目标 Server 的 URL</p></li><li><p><strong>用户名/密码</strong>：无需填写</p></li><li><p><strong>证书/密钥</strong>：无需添加</p></li></ul></li><li><p>根据测点信息添加 <strong>Groups</strong> 和 <strong>Tags</strong>。</p></li></ol><h2 id="连接-opc-ua-server-证书-密钥-匿名登录" tabindex="-1">连接 OPC UA Server（证书/密钥 + 匿名登录） <a class="header-anchor" href="#连接-opc-ua-server-证书-密钥-匿名登录" aria-label="Permalink to “连接 OPC UA Server（证书/密钥 + 匿名登录）”">​</a></h2><ol><li><p>参考<a href="./policy.html">连接策略</a>生成或转换证书/密钥；</p></li><li><p>Prosys OPC UA Simulation Server 界面中切换到 <strong>Endpoints</strong> -&gt; <strong>Security Modes</strong> 取消选择 <strong>None</strong>，选择 <strong>Sign</strong> 和 <strong>Sign&amp;Encrypt</strong>；</p></li><li><p>Prosys OPC UA Simulation Server 界面中切换到 <strong>Users</strong> -&gt; <strong>User Authentication Methods</strong> 取消选择 <strong>Username&amp;Password</strong>、<strong>Certificate</strong> 和 <strong>IssuedToken/External System</strong>，选择 <strong>Anonymous</strong>；</p></li><li><p>保存设置并重新启动 Prosys OPC UA Simulation Server。</p></li></ol><h2 id="配置-gridbeat-1" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat-1" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li><p>通过 UaExpert 软件查看 Ignition 测点信息， 参考 <a href="./uaexpert.html">配置 UaExpert</a>。 <img src="'+r+'" alt="prosys-5"></p></li><li><p>GridBeat 新增南向 OPC UA 设备，打开 <strong>设备配置</strong>，并进行如下设置后，启动设备连接。</p><ul><li><strong>端点 URL</strong>：填写目标 Server 的 URL</li><li><strong>用户名/密码</strong>：无需填写</li><li><strong>证书/密钥</strong>：添加证书和密钥</li></ul></li><li><p>Prosys OPC UA Simulation Server 界面中切换到 <strong>Certificates</strong>，将列表中的客户端证书设置为信任。 <img src="'+l+'" alt="prosys-3"></p></li><li><p>根据测点信息添加 <strong>Groups</strong> 和 <strong>Tags</strong>。</p></li></ol><h2 id="连接-opc-ua-server-用户名-密码" tabindex="-1">连接 OPC UA Server（用户名/密码） <a class="header-anchor" href="#连接-opc-ua-server-用户名-密码" aria-label="Permalink to “连接 OPC UA Server（用户名/密码）”">​</a></h2><ol><li>Prosys OPC UA Simulation Server 界面中切换到 <strong>Endpoints</strong> -&gt; <strong>Security Modes</strong> 取消选择 <strong>None</strong>，选择 <strong>Sign</strong> 和 <strong>Sign&amp;Encrypt</strong>。</li><li>Prosys OPC UA Simulation Server 界面中切换到 <strong>Users</strong> -&gt; <strong>User Authentication Methods</strong> 取消选择 <strong>Anonymous</strong>、<strong>Certificate</strong> 和 <strong>IssuedToken/External System</strong>，选择 <strong>Username&amp;Password</strong>，添加自定义用户名/密码。 <img src="'+g+'" alt="prosys-4"></li><li>保存设置并重新启动 Prosys OPC UA Simulation Server。</li></ol><h2 id="配置-gridbeat-2" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat-2" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li><p>通过 UaExpert 软件查看 Ignition 测点信息， 参考 <a href="./uaexpert.html">配置 UaExpert</a>。 <img src="'+r+'" alt="prosys-5"></p></li><li><p>GridBeat 新增南向 OPC UA 设备，打开 <strong>设备配置</strong>，并进行如下设置后，启动设备连接。</p><ul><li><p><strong>端点 URL</strong>：填写目标 Server 的 URL</p></li><li><p><strong>用户名/密码</strong>：填写用户名和密码</p></li><li><p><strong>证书/密钥</strong>：无需添加</p></li><li><p><strong>更新模式</strong>：可以修改为 Subscribe 或 Read&amp;Subscribe，以 OPC UA 订阅方式获取数据</p></li></ul></li><li><p>Prosys OPC UA Simulation Server 界面中切换到 <strong>Certificates</strong>，将列表中的客户端证书设置为信任。</p></li><li><p>根据测点信息添加 <strong>Groups</strong> 和 <strong>Tags</strong>。</p></li></ol><h2 id="连接-opc-ua-server-证书-密钥-用户名-密码" tabindex="-1">连接 OPC UA Server（证书/密钥 + 用户名/密码） <a class="header-anchor" href="#连接-opc-ua-server-证书-密钥-用户名-密码" aria-label="Permalink to “连接 OPC UA Server（证书/密钥 + 用户名/密码）”">​</a></h2><p>用户名/密码以及证书/密钥设置参考</p><ul><li><a href="#连接-opc-ua-server-证书-密钥-匿名登录">连接 OPC UA Server（证书/密钥 + 匿名登录）</a></li><li><a href="#连接-opc-ua-server-用户名-密码">连接 OPC UA Server（用户名/密码登录）</a></li></ul><h2 id="配置-gridbeat-3" tabindex="-1">配置 GridBeat <a class="header-anchor" href="#配置-gridbeat-3" aria-label="Permalink to “配置 GridBeat”">​</a></h2><ol><li><p>通过 UaExpert 软件查看 Ignition 测点信息， 参考 <a href="./uaexpert.html">配置 UaExpert</a>。 <img src="'+r+'" alt="prosys-5"></p></li><li><p>GridBeat 新增南向 OPC UA 设备，打开 <strong>设备配置</strong>，并进行如下设置后，启动设备连接。</p><ul><li><p><strong>端点 URL</strong>：填写目标 Server 的 URL</p></li><li><p><strong>用户名/密码</strong>：填写用户名和密码</p></li><li><p><strong>证书/密钥</strong>：添加证书和密钥</p></li><li><p><strong>更新模式</strong>：可以修改为 Subscribe 或 Read&amp;Subscribe，以 OPC UA 订阅方式获取数据</p></li></ul></li><li><p>Prosys OPC UA Simulation Server 界面中切换到 <strong>Certificates</strong>，将列表中的客户端证书设置为信任。</p></li><li><p>根据测点信息添加 <strong>Groups</strong> 和 <strong>Tags</strong>。</p></li></ol><h2 id="测试点位" tabindex="-1">测试点位 <a class="header-anchor" href="#测试点位" aria-label="Permalink to “测试点位”">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>地址</th><th>属性</th><th>类型</th></tr></thead><tbody><tr><td>Counter</td><td>3!1001</td><td>Read</td><td>INT32</td></tr><tr><td>Random</td><td>3!1002</td><td>Read</td><td>DOUBLE</td></tr><tr><td>Sawtooth</td><td>3!1003</td><td>Read</td><td>DOUBLE</td></tr><tr><td>Sinusoid</td><td>3!1004</td><td>Read</td><td>DOUBLE</td></tr><tr><td>Square</td><td>3!1005</td><td>Read</td><td>DOUBLE</td></tr><tr><td>Triangle</td><td>3!1006</td><td>Read</td><td>DOUBLE</td></tr></tbody></table>',22)])])}const v=s(d,[["render",p]]);export{U as __pageData,v as default};
