import{_ as t,c as a,o as r,ah as i,ml as c}from"./chunks/framework.DGOuNAW6.js";const m=JSON.parse('{"title":"许可证政策","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/introduction/license/license-policy.md","filePath":"zh_CN/introduction/license/license-policy.md"}'),o={name:"zh_CN/introduction/license/license-policy.md"};function s(l,e,n,p,d,h){return r(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="许可证政策" tabindex="-1">许可证政策 <a class="header-anchor" href="#许可证政策" aria-label="Permalink to “许可证政策”">​</a></h1><p>GridBeat 是一个开源项目。我们鼓励我们的社区开发自己的插件模块。</p><p>核心架构，仪表板和一些驱动模块（例如 modbus-tcp，mqtt 和 eKuiper 等）是在 LGPLv3 许可下开源的。您无需安装 EMQ 许可证即可运行这些开源模块。</p><p>对于商业模块，GridBeat 提供了 30 个点（30 个连接和 30 个数据标签）的免费额度。您可在不安装 EMQ 许可证的情况下，运行这些商业模块。超出免费额度后，则必须安装有效的试用版或官方 EMQ 许可证。</p><div class="tip custom-block"><p class="custom-block-title">注意</p><p>Fanuc Focas Ethernet 和 Mitsubishi CNC 插件不在 30 点永久免费的范围内。如希望体验，可直接 <a href="https://www.emqx.com/zh/contact?product=gridbeat" target="_blank" rel="noreferrer">联系我们</a>。</p></div><h2 id="申请许可证" tabindex="-1">申请许可证 <a class="header-anchor" href="#申请许可证" aria-label="Permalink to “申请许可证”">​</a></h2><p>试用的 EMQ 许可证可从<a href="https://www.emqx.com/zh/apply-licenses/gridbeat" target="_blank" rel="noreferrer"> EMQ 官方网站</a>上下载 。所有可用的模块都可以在 100 个连接和 1000 个数据标签的限制下试用 15 天。 如果试用 EMQ 许可证过期，您可以通过我们的官网重新申请试用 EMQ 许可证。 但是，一个邮箱最多只能申请两次试用许可证。</p><div class="tip custom-block"><p class="custom-block-title">注意</p><p>官网申请试用许可证时，必须使用硬件标识进行设备的绑定。您可直接<a href="https://www.emqx.com/zh/contact?product=gridbeat" target="_blank" rel="noreferrer">联系我们</a>申请不需要硬件标识绑定设备的许可证，或申请正式许可证。</p></div><h2 id="管理许可证" tabindex="-1">管理许可证 <a class="header-anchor" href="#管理许可证" aria-label="Permalink to “管理许可证”">​</a></h2><p>申请许可证后，您可在登录 GridBeat 后，点击页面顶部工具栏的 系统信息 -&gt; License 上传许可证，或管理许可证文件。</p><p><img src="'+c+'" alt="image-20230717142817902"></p>',11)])])}const b=t(o,[["render",s]]);export{m as __pageData,b as default};
